import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PropiedadForm from './PropiedadForm';

const API_URL = 'http://localhost:3000/propiedades'; // Ensure this matches your backend port

function PropiedadesList() {
  const [propiedades, setPropiedades] = useState([]);
  const [editingPropiedad, setEditingPropiedad] = useState(null);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchPropiedades();
  }, []);

  const fetchPropiedades = async () => {
    try {
      const response = await axios.get(API_URL);
      setPropiedades(response.data);
    } catch (error) {
      console.error('Error fetching propiedades:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchPropiedades(); // Refresh the list
    } catch (error) {
      console.error('Error deleting propiedad:', error);
    }
  };

  const handleEdit = (propiedad) => {
    setEditingPropiedad(propiedad);
    setShowForm(true);
  };

  const handleFormSubmit = () => {
    fetchPropiedades(); // Refresh list after add/edit
    setEditingPropiedad(null); // Clear editing state
    setShowForm(false); // Hide the form
  };

  const handleCancelForm = () => {
    setEditingPropiedad(null);
    setShowForm(false);
  };

  return (
    <div className="propiedades-list">
      <button onClick={() => setShowForm(true)}>Agregar Nueva Propiedad</button>

      {showForm && (
        <PropiedadForm
          propiedad={editingPropiedad}
          onSuccess={handleFormSubmit}
          onCancel={handleCancelForm}
        />
      )}

      <h2>Listado de Propiedades</h2>
      {propiedades.length === 0 ? (
        <p>No hay propiedades disponibles.</p>
      ) : (
        <ul>
          {propiedades.map((propiedad) => (
            <li key={propiedad._id} className="propiedad-item">
              <h3>{propiedad.titulo}</h3>
              <p><strong>Descripción:</strong> {propiedad.descripcion}</p>
              <p><strong>Dirección:</strong> {propiedad.direccion}</p>
              <p><strong>Precio:</strong> ${propiedad.precio}</p>
              <p><strong>Tipo:</strong> {propiedad.tipo}</p>
              <p><strong>Habitaciones:</strong> {propiedad.habitaciones}</p>
              <p><strong>Baños:</strong> {propiedad.banos}</p>
              <p><strong>Metros Cuadrados:</strong> {propiedad.metrosCuadrados}m²</p>
              <p><strong>Disponible:</strong> {propiedad.disponible ? 'Sí' : 'No'}</p>
              {propiedad.imagenes && propiedad.imagenes.length > 0 && (
                <div>
                  <strong>Imágenes:</strong>
                  <div className="imagenes-container">
                    {propiedad.imagenes.map((img, index) => (
                      <img key={index} src={img} alt={`Imagen ${index}`} style={{ width: '100px', height: 'auto', margin: '5px' }} />
                    ))}
                  </div>
                </div>
              )}
              <div className="propiedad-actions">
                <button onClick={() => handleEdit(propiedad)}>Editar</button>
                <button onClick={() => handleDelete(propiedad._id)}>Eliminar</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PropiedadesList;
