import React, { useState, useEffect } from 'react'; // Importamos useEffect y useState

// Función auxiliar para formatear números
const formatNumber = (number) => {
    if (number === null || number === undefined || isNaN(number)) {
        return 'N/A';
    }

    // Convertir a número si es string
    const num = typeof number === 'string' ? parseFloat(number) : number;

    // Formatear con separadores de miles y decimales si es necesario
    return new Intl.NumberFormat('es-AR', {
        minimumFractionDigits: 0,
        maximumFractionDigits: 2
    }).format(num);
};

// Función auxiliar para formatear precios
const formatPrice = (precio) => {
    if (!precio || !precio.monto) {
        return 'Precio a consultar';
    }

    const montoFormateado = formatNumber(precio.monto);
    const simbolo = precio.moneda === 'USD' ? 'US$' : '$';

    return `${simbolo} ${montoFormateado} ${precio.moneda}`;
};

function PropiedadDetail({ propiedad, onClose }) {
    // Estado para controlar qué imagen se está mostrando actualmente
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    // Reiniciar el índice de imagen cuando la propiedad cambie
    useEffect(() => {
        setCurrentImageIndex(0);
    }, [propiedad]);

    if (!propiedad) {
        return null;
    }

    // Obtener la URL de la imagen principal a mostrar
    const imageUrl = propiedad.imagenes && propiedad.imagenes.length > 0
        ? `http://localhost:3000${propiedad.imagenes[currentImageIndex].url}`
        : "https://via.placeholder.com/400x300?text=No+Image";

    // Funciones para navegar entre imágenes
    const goToNextImage = () => {
        if (propiedad.imagenes && propiedad.imagenes.length > 0) {
            setCurrentImageIndex((prevIndex) =>
                (prevIndex + 1) % propiedad.imagenes.length
            );
        }
    };

    const goToPreviousImage = () => {
        if (propiedad.imagenes && propiedad.imagenes.length > 0) {
            setCurrentImageIndex((prevIndex) =>
                (prevIndex - 1 + propiedad.imagenes.length) % propiedad.imagenes.length
            );
        }
    };

    return (
        <div className="propiedad-detail-modal">
            <div className="propiedad-detail-content">
                <h2>{propiedad.titulo}</h2>

                <div className="image-gallery-container">
                    <img
                        src={imageUrl}
                        alt={propiedad.titulo}
                        className="propiedad-detail-main-image"
                        onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/400x300?text=No+Image" }}
                    />
                    {propiedad.imagenes && propiedad.imagenes.length > 1 && (
                        <div className="gallery-controls">
                            <button onClick={goToPreviousImage} className="gallery-nav-button prev">
                                &lt;
                            </button>
                            <button onClick={goToNextImage} className="gallery-nav-button next">
                                &gt;
                            </button>
                            <div className="image-counter">
                                {currentImageIndex + 1} / {propiedad.imagenes.length}
                            </div>
                        </div>
                    )}
                </div>


                <div className="detail-info">
                    <p><strong>Transacción:</strong> {propiedad.transaccion || 'N/A'}</p>
                    <p><strong>Dirección:</strong> {propiedad.direccion}</p>
                    {/* Campos eliminados: Ciudad, País, Amenidades */}
                    {/* <p><strong>Ciudad:</strong> {propiedad.ciudad || 'N/A'}</p> */}
                    {/* <p><strong>País:</strong> {propiedad.pais || 'N/A'}</p> */}
                    <p><strong>Precio:</strong> {propiedad.precio?.monto ? `$${propiedad.precio.monto.toLocaleString('es-AR')} ${propiedad.precio.moneda}` : 'N/A'}</p>
                    <p><strong>Tipo:</strong> {propiedad.tipo}</p>
                    <p><strong>Habitaciones:</strong> {propiedad.habitaciones}</p>
                    <p><strong>Baños:</strong> {propiedad.banos}</p>
                    <p><strong>Ambientes:</strong> {propiedad.ambientes || 'N/A'}</p>
                    <p><strong>Metros Cuadrados:</strong> {propiedad.metrosCuadrados}m²</p>
                    <p className="detail-description"><strong>Descripción:</strong> {propiedad.descripcion}</p>
                    {/* Campo eliminado: Amenidades */}
                    {/* <p><strong>Amenidades:</strong> {propiedad.amenidades && propiedad.amenidades.length > 0 ? propiedad.amenidades.join(', ') : 'N/A'}</p> */}
                    <p><strong>Disponibilidad:</strong> {propiedad.disponible ? 'Sí' : 'No'}</p>
                </div>

                {propiedad.imagenes && propiedad.imagenes.length > 1 && (
                    <div className="detail-thumbnails">
                        <h4>Miniaturas:</h4>
                        <div className="thumbnail-container">
                            {propiedad.imagenes.map((imageObj, index) => (
                                <img
                                    key={index}
                                    src={`http://localhost:3000${imageObj.url}`}
                                    alt={`${propiedad.titulo} ${index + 1}`}
                                    className={`propiedad-detail-thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                                    onClick={() => setCurrentImageIndex(index)}
                                    onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/100x75?text=No+Image" }}
                                />
                            ))}
                        </div>
                    </div>
                )}

                <button onClick={onClose} className="btn-close-detail">
                    Cerrar
                </button>
            </div>
        </div>
    );
}

export default PropiedadDetail;
