import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PropiedadForm from './PropiedadForm';
import {
  Box,
  Heading,
  Text,
  Button,
  VStack,
  HStack,
  SimpleGrid,
  Spinner,
  Tag,
  Badge,
  Image,
  Flex
} from '@chakra-ui/react';
import { useToast } from '@chakra-ui/toast'; // Correct import for useToast
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
} from '@chakra-ui/modal'; // Correct imports for Modal components
import { useDisclosure } from '@chakra-ui/hooks'; // useDisclosure is also from @chakra-ui/hooks
import { FaPlus, FaEdit, FaTrash } from 'react-icons/fa';

const API_URL = 'http://localhost:3000/propiedades'; // Make sure this matches your backend port

function PropiedadesList() {
  const [propiedades, setPropiedades] = useState([]);
  const [editingPropiedad, setEditingPropiedad] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const toast = useToast();
  const { isOpen, onOpen, onClose } = useDisclosure(); // For the form modal

  useEffect(() => {
    fetchPropiedades();
  }, []);

  const fetchPropiedades = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(API_URL);
      setPropiedades(response.data);
    } catch (error) {
      console.error('Error fetching propiedades:', error);
      toast({
        title: 'Error al cargar propiedades.',
        description: error.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      toast({
        title: 'Propiedad eliminada.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
      fetchPropiedades(); // Refresh the list
    } catch (error) {
      console.error('Error deleting propiedad:', error);
      toast({
        title: 'Error al eliminar propiedad.',
        description: error.response?.data?.error || error.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const handleEdit = (propiedad) => {
    setEditingPropiedad(propiedad);
    onOpen(); // Open the modal for editing
  };

  const handleFormSubmit = () => {
    fetchPropiedades(); // Refresh list after add/edit
    setEditingPropiedad(null); // Clear editing state
    onClose(); // Close the modal
  };

  const handleCancelForm = () => {
    setEditingPropiedad(null);
    onClose();
  };

  return (
    <Box p={5} shadow="md" borderWidth="1px" borderRadius="lg" bg="white">
      <HStack justifyContent="space-between" mb={6}>
        <Heading as="h2" size="lg" color="brand.700">
          Listado de Propiedades
        </Heading>
        <Button leftIcon={<FaPlus />} colorScheme="brand" onClick={() => { setEditingPropiedad(null); onOpen(); }}>
          Agregar Propiedad
        </Button>
      </HStack>

      {isLoading ? (
        <Flex justify="center" align="center" minH="200px">
          <Spinner size="xl" color="brand.500" />
          <Text ml={3}>Cargando propiedades...</Text>
        </Flex>
      ) : propiedades.length === 0 ? (
        <Text textAlign="center" py={10} fontSize="lg" color="gray.600">
          No hay propiedades disponibles. ¡Agrega una!
        </Text>
      ) : (
        <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing={6}>
          {propiedades.map((propiedad) => (
            <Box
              key={propiedad._id}
              p={5}
              shadow="md"
              borderWidth="1px"
              borderRadius="lg"
              bg="white"
              _hover={{ transform: 'translateY(-5px)', shadow: 'xl' }}
              transition="all 0.2s ease-in-out"
            >
              {propiedad.imagenes && propiedad.imagenes.length > 0 && (
                <Image
                  src={propiedad.imagenes[0]}
                  alt={propiedad.titulo}
                  borderRadius="md"
                  mb={4}
                  objectFit="cover"
                  h="200px"
                  w="100%"
                />
              )}
              <Heading as="h3" size="md" mb={2} color="brand.600">
                {propiedad.titulo}
              </Heading>
              <Text fontSize="sm" color="gray.600" mb={3} noOfLines={2}>
                {propiedad.descripcion}
              </Text>
              <Text mb={1}><Text as="span" fontWeight="bold">Dirección:</Text> {propiedad.direccion}</Text>
              <Text mb={1}><Text as="span" fontWeight="bold">Precio:</Text> ${propiedad.precio.toLocaleString()}</Text>
              <HStack spacing={2} mb={3}>
                <Tag size="sm" colorScheme="blue">{propiedad.tipo}</Tag>
                <Tag size="sm" colorScheme="green">{propiedad.habitaciones} Habitaciones</Tag>
                <Tag size="sm" colorScheme="purple">{propiedad.banos} Baños</Tag>
                <Tag size="sm" colorScheme="orange">{propiedad.metrosCuadrados}m²</Tag>
              </HStack>
              <Badge colorScheme={propiedad.disponible ? 'teal' : 'red'} mb={4}>
                {propiedad.disponible ? 'Disponible' : 'No Disponible'}
              </Badge>

              <HStack mt={4} spacing={3} justifyContent="flex-end">
                <Button leftIcon={<FaEdit />} size="sm" colorScheme="blue" onClick={() => handleEdit(propiedad)}>
                  Editar
                </Button>
                <Button leftIcon={<FaTrash />} size="sm" colorScheme="red" onClick={() => handleDelete(propiedad._id)}>
                  Eliminar
                </Button>
              </HStack>
            </Box>
          ))}
        </SimpleGrid>
      )}

      <Modal isOpen={isOpen} onClose={onClose} size="xl">
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>{editingPropiedad ? 'Editar Propiedad' : 'Agregar Nueva Propiedad'}</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <PropiedadForm
              propiedad={editingPropiedad}
              onSuccess={handleFormSubmit}
              onCancel={handleCancelForm}
            />
          </ModalBody>
          <ModalFooter>
            {/* Footer can be used for extra actions or just leave it empty if buttons are in form */}
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
}

export default PropiedadesList;
