import React, { useState, useEffect } from 'react';
import PropiedadesList from './components/PropiedadesList';
import AuthForm from './components/AuthForm';
import ImageCarousel from './components/ImageCarousel.js'; // Make sure this path is correct!
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAuthForm, setShowAuthForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [carouselImages, setCarouselImages] = useState([]);

  // ** IMPORTANT: REPLACE WITH YOUR ACTUAL UNSPLASH ACCESS KEY **
  // In a real project, move this to an environment variable (.env file)
  const UNSPLASH_ACCESS_KEY = 'TU_ACCES_KEY_DE_UNSPLASH';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }

    const fetchCarouselImages = async () => {
      try {
        const query = 'real estate property interior';
        const perPage = 3;
        const response = await fetch(
          `https://api.unsplash.com/search/photos?query=${query}&per_page=${perPage}&client_id=${UNSPLASH_ACCESS_KEY}`
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        const imagesUrls = data.results.map(photo => photo.urls.regular);
        setCarouselImages(imagesUrls);

      } catch (error) {
        console.error('Error al cargar imágenes del carrusel desde Unsplash:', error);
        setCarouselImages([
          'https://via.placeholder.com/800x400?text=Error+al+cargar+imagen+1',
          'https://via.placeholder.com/800x400?text=Error+al+cargar+imagen+2',
          'https://via.placeholder.com/800x400?text=Error+al+cargar+imagen+3',
        ]);
      }
    };

    fetchCarouselImages();
  }, []);

  // --- ENSURE THESE FUNCTIONS ARE PRESENT AND CORRECTLY DEFINED ---
  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
    setShowAuthForm(false);
  };

  const handleAuthCancel = () => {
    setShowAuthForm(false);
    setIsRegistering(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    alert('Has cerrado sesión exitosamente.');
  };

  const handleOpenRegisterForm = () => {
    if (isAuthenticated) {
      setShowAuthForm(true);
      setIsRegistering(true);
    } else {
      alert('Solo los usuarios autenticados pueden registrar nuevas cuentas. Por favor, inicia sesión primero.');
      setShowAuthForm(true);
      setIsRegistering(false);
    }
  };
  // --- END OF FUNCTIONS TO CHECK ---


  return (
    <div className="App">
      <header>
        <img src="/logo-inmobiliaria.png" alt="Logo de la Inmobiliaria" className="app-logo" />
        <div className="auth-buttons">
          {isAuthenticated ? (
            <>
              <span className="welcome-message">¡Bienvenido!</span>
              <button onClick={handleOpenRegisterForm} className="btn-register-new-user">
                Registrar Nuevo Usuario
              </button>
              <button onClick={handleLogout} className="btn-logout">
                Cerrar Sesión
              </button>
            </>
          ) : (
            <>
              <button onClick={() => { setShowAuthForm(true); setIsRegistering(false); }} className="btn-login primary-login-btn">
                Iniciar Sesión
              </button>
            </>
          )}
        </div>
      </header>
      <main>
        {carouselImages.length > 0 && <ImageCarousel images={carouselImages} />}
        <PropiedadesList
          isAuthenticated={isAuthenticated}
          setShowAuthForm={setShowAuthForm}
          setIsRegistering={setIsRegistering}
        />
      </main>

      {showAuthForm && (
        <AuthForm
          onAuthSuccess={handleAuthSuccess}
          onCancel={handleAuthCancel}
          isRegister={isRegistering}
        />
      )}
    </div>
  );
}

export default App;
