import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3000/propiedades';

function PropiedadForm({ propiedad, onSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: '',
    tipo: '',
    habitaciones: '',
    banos: '',
    metrosCuadrados: '',
    disponible: true,
    imagenes: '',
  });
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [focusedField, setFocusedField] = useState(null);

  useEffect(() => {
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio || '',
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones || '',
        banos: propiedad.banos || '',
        metrosCuadrados: propiedad.metrosCuadrados || '',
        disponible: propiedad.disponible !== undefined ? propiedad.disponible : true,
        imagenes: (propiedad.imagenes || []).join(', '),
      });
    } else {
      setFormData({
        titulo: '',
        descripcion: '',
        direccion: '',
        precio: '',
        tipo: '',
        habitaciones: '',
        banos: '',
        metrosCuadrados: '',
        disponible: true,
        imagenes: '',
      });
    }
    setError(null);
  }, [propiedad]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
    // Limpiar error cuando el usuario empiece a escribir
    if (error) setError(null);
  };

  const handleFocus = (fieldName) => {
    setFocusedField(fieldName);
  };

  const handleBlur = () => {
    setFocusedField(null);
  };

  const validateForm = () => {
    const requiredFields = ['titulo', 'descripcion', 'direccion', 'precio', 'tipo', 'habitaciones', 'banos', 'metrosCuadrados'];
    for (let field of requiredFields) {
      if (!formData[field] || formData[field].toString().trim() === '') {
        return `El campo ${field} es requerido`;
      }
    }

    if (parseFloat(formData.precio) <= 0) {
      return 'El precio debe ser mayor a 0';
    }

    if (parseInt(formData.habitaciones) < 0) {
      return 'El número de habitaciones no puede ser negativo';
    }

    if (parseInt(formData.banos) < 0) {
      return 'El número de baños no puede ser negativo';
    }

    if (parseFloat(formData.metrosCuadrados) <= 0) {
      return 'Los metros cuadrados deben ser mayor a 0';
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setIsSubmitting(true);

    const dataToSend = {
      ...formData,
      precio: parseFloat(formData.precio),
      habitaciones: parseInt(formData.habitaciones),
      banos: parseInt(formData.banos),
      metrosCuadrados: parseFloat(formData.metrosCuadrados),
      imagenes: formData.imagenes
        .split(',')
        .map(img => img.trim())
        .filter(img => img !== ''),
    };

    try {
      if (propiedad) {
        await axios.put(`${API_URL}/${propiedad._id}`, dataToSend);
      } else {
        await axios.post(API_URL, dataToSend);
      }
      onSuccess();
    } catch (err) {
      console.error('Error submitting form:', err.response ? err.response.data : err.message);
      setError(
        err.response?.data?.error || 
        err.response?.data?.message || 
        'Ocurrió un error inesperado. Por favor, inténtalo de nuevo.'
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  const tiposPropiedad = [
    'Casa', 'Departamento', 'Oficina', 'Local Comercial', 
    'Terreno', 'Bodega', 'Penthouse', 'Duplex'
  ];

  const FormField = ({ 
    label, 
    name, 
    type = 'text', 
    required = false, 
    options = null,
    placeholder = '',
    min = null,
    step = null
  }) => (
    <div className="form-field">
      <label className={`form-label ${focusedField === name ? 'focused' : ''}`}>
        {label} {required && <span className="required-indicator">*</span>}
      </label>
      {options ? (
        <select
          name={name}
          value={formData[name]}
          onChange={handleChange}
          onFocus={() => handleFocus(name)}
          onBlur={handleBlur}
          required={required}
          className={`form-select ${focusedField === name ? 'focused' : ''}`}
        >
          <option value="">Seleccionar {label.toLowerCase()}</option>
          {options.map(option => (
            <option key={option} value={option}>{option}</option>
          ))}
        </select>
      ) : type === 'textarea' ? (
        <textarea
          name={name}
          value={formData[name]}
          onChange={handleChange}
          onFocus={() => handleFocus(name)}
          onBlur={handleBlur}
          required={required}
          placeholder={placeholder}
          className={`form-textarea ${focusedField === name ? 'focused' : ''}`}
          rows={4}
        />
      ) : (
        <input
          type={type}
          name={name}
          value={formData[name]}
          onChange={handleChange}
          onFocus={() => handleFocus(name)}
          onBlur={handleBlur}
          required={required}
          placeholder={placeholder}
          min={min}
          step={step}
          className={`form-input ${focusedField === name ? 'focused' : ''}`}
        />
      )}
    </div>
  );

  return (
    <div className="propiedad-form-container">
      <div className="form-header">
        <h3 className="form-title">
          {propiedad ? '✏️ Editar Propiedad' : '➕ Agregar Nueva Propiedad'}
        </h3>
        <p className="form-subtitle">
          {propiedad 
            ? 'Modifica los datos de la propiedad seleccionada' 
            : 'Completa la información para registrar una nueva propiedad'
          }
        </p>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-icon">⚠️</div>
          <div className="error-text">{error}</div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="propiedad-form modern-form">
        <div className="form-grid">
          <div className="form-section">
            <h4 className="section-title">📝 Información Básica</h4>

            <FormField
              label="Título"
              name="titulo"
              required
              placeholder="Ej: Casa moderna en zona residencial"
            />

            <FormField
              label="Descripción"
              name="descripcion"
              type="textarea"
              required
              placeholder="Describe las características principales de la propiedad..."
            />

            <FormField
              label="Dirección"
              name="direccion"
              required
              placeholder="Ej: Av. Principal 123, Zona Norte"
            />

            <FormField
              label="Tipo de Propiedad"
              name="tipo"
              required
              options={tiposPropiedad}
            />
          </div>

          <div className="form-section">
            <h4 className="section-title">💰 Detalles Comerciales</h4>

            <FormField
              label="Precio"
              name="precio"
              type="number"
              required
              placeholder="0"
              min="0"
              step="0.01"
            />

            <div className="form-row">
              <FormField
                label="Habitaciones"
                name="habitaciones"
                type="number"
                required
                placeholder="0"
                min="0"
              />

              <FormField
                label="Baños"
                name="banos"
                type="number"
                required
                placeholder="0"
                min="0"
              />
            </div>

            <FormField
              label="Metros Cuadrados"
              name="metrosCuadrados"
              type="number"
              required
              placeholder="0"
              min="0"
              step="0.01"
            />

            <div className="checkbox-field">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  name="disponible"
                  checked={formData.disponible}
                  onChange={handleChange}
                  className="checkbox-input"
                />
                <span className="checkbox-custom"></span>
                <span className="checkbox-text">
                  ✅ Propiedad disponible para mostrar
                </span>
              </label>
            </div>
          </div>
        </div>

        <div className="form-section full-width">
          <h4 className="section-title">🖼️ Imágenes</h4>
          <FormField
            label="URLs de Imágenes"
            name="imagenes"
            placeholder="https://ejemplo.com/imagen1.jpg, https://ejemplo.com/imagen2.jpg"
          />
          <p className="field-hint">
            Separa múltiples URLs con comas. Las imágenes deben ser accesibles públicamente.
          </p>
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={onCancel}
            className="btn-secondary"
            disabled={isSubmitting}
          >
            ❌ Cancelar
          </button>
          <button
            type="submit"
            className={`btn-primary ${isSubmitting ? 'loading' : ''}`}
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="loading-spinner"></span>
                Guardando...
              </>
            ) : (
              <>
                {propiedad ? '💾 Actualizar' : '➕ Guardar'}
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default PropiedadForm;
