import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3000/propiedades'; // Asegúrate que coincida con el puerto de tu backend

function PropiedadEditForm({ propiedad, onSuccess, onCancel }) {
  // Asegúrate de que `propiedad` no sea null al inicializar el estado
  // Si `propiedad` es null al inicio, puedes manejar un estado inicial vacío o lanzar un error.
  // Para este caso, asumimos que siempre recibiremos una `propiedad` válida para editar.
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: '',
    tipo: '',
    habitaciones: '',
    banos: '',
    metrosCuadrados: '',
    disponible: true,
  });
  const [imagenArchivo, setImagenArchivo] = useState(null); // Estado para el archivo de imagen a subir
  const [imagenPreview, setImagenPreview] = useState(''); // Estado para la previsualización de la imagen
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Carga los datos de la propiedad a editar cuando el componente se monta
  // o cuando la `propiedad` prop cambia.
  useEffect(() => {
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio !== undefined ? String(propiedad.precio) : '',
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones !== undefined ? String(propiedad.habitaciones) : '',
        banos: propiedad.banos !== undefined ? String(propiedad.banos) : '',
        metrosCuadrados: propiedad.metrosCuadrados !== undefined ? String(propiedad.metrosCuadrados) : '',
        disponible: propiedad.disponible !== undefined ? propiedad.disponible : true,
      });
      if (propiedad.imagenes && propiedad.imagenes.length > 0) {
        setImagenPreview(`http://localhost:3000${propiedad.imagenes[0]}`);
      } else {
        setImagenPreview('');
      }
      setImagenArchivo(null); // Limpiar archivo seleccionado al cargar modo edición
    } else {
      // Si por alguna razón `propiedad` es null, reinicia el formulario
      setFormData({
        titulo: '', descripcion: '', direccion: '', precio: '', tipo: '',
        habitaciones: '', banos: '', metrosCuadrados: '', disponible: true,
      });
      setImagenArchivo(null);
      setImagenPreview('');
    }
    setError(null);
  }, [propiedad]);

  // Maneja los cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
    if (error) setError(null);
  };

  // Manejador para el input de tipo 'file' (subida de imagen)
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImagenArchivo(file);
      setImagenPreview(URL.createObjectURL(file));
      setError(null);
    } else {
      setImagenArchivo(null);
      setImagenPreview('');
    }
  };

  // Valida los campos del formulario antes de enviar
  const validateForm = () => {
    const requiredFields = ['titulo', 'descripcion', 'direccion', 'precio', 'tipo', 'habitaciones', 'banos', 'metrosCuadrados'];
    for (let field of requiredFields) {
      if (!formData[field] || formData[field].toString().trim() === '') {
        return `El campo "${field}" es requerido.`;
      }
    }

    const precio = parseFloat(formData.precio);
    if (isNaN(precio) || precio <= 0) {
      return 'El precio debe ser un número válido mayor a 0.';
    }

    const habitaciones = parseInt(formData.habitaciones);
    if (isNaN(habitaciones) || habitaciones < 0) {
      return 'El número de habitaciones debe ser un número entero no negativo.';
    }

    const banos = parseInt(formData.banos);
    if (isNaN(banos) || banos < 0) {
      return 'El número de baños debe ser un número entero no negativo.';
    }

    const metrosCuadrados = parseFloat(formData.metrosCuadrados);
    if (isNaN(metrosCuadrados) || metrosCuadrados <= 0) {
      return 'Los metros cuadrados deben ser un número válido mayor a 0.';
    }

    // En modo edición, la imagen no es estrictamente obligatoria si ya existe una.
    // Solo requerimos una imagen si la propiedad NO tiene una existente Y NO se sube una nueva.
    if ((!propiedad.imagenes || propiedad.imagenes.length === 0) && !imagenArchivo) {
      return 'Esta propiedad no tiene imagen. Debes subir una imagen nueva.';
    }

    return null;
  };

  // Maneja el envío del formulario para ACTUALIZAR la propiedad
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setIsSubmitting(true);

    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    if (imagenArchivo) {
      formDataToSend.append('imagen', imagenArchivo);
    } else if (propiedad.imagenes && propiedad.imagenes.length > 0) {
      // Si no se subió una nueva imagen, pero la propiedad ya tiene una,
      // reenviamos la URL existente al backend para que la conserve.
      formDataToSend.append('imagenes', propiedad.imagenes[0]);
    } else {
      // Si no hay imagen existente y no se sube una nueva, envía un string vacío
      // para que el backend sepa que debe borrar o no asignar una imagen.
      formDataToSend.append('imagenes', '');
    }

    try {
      // Petición PUT para actualizar una propiedad existente
      await axios.put(`${API_URL}/${propiedad._id}`, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      onSuccess(); // Llama a la función de éxito
    } catch (err) {
      console.error('Error al actualizar la propiedad (frontend):', err.response ? err.response.data : err.message);
      setError(
        err.response?.data?.error ||
        err.response?.data?.message ||
        'Ocurrió un error inesperado al actualizar. Por favor, inténtalo de nuevo.'
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  const tiposPropiedad = [
    'Casa', 'Departamento', 'Oficina', 'Local Comercial',
    'Terreno', 'Bodega', 'Penthouse', 'Duplex', 'Estudio', 'Nave Industrial'
  ];

  return (
    <div className="propiedad-form-container">
      <div className="form-header">
        <h3 className="form-title">Editar Propiedad</h3>
        <p className="form-subtitle">Modifica los datos de la propiedad seleccionada.</p>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-icon">⚠</div>
          <div className="error-text">{error}</div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="propiedad-form">
        <div className="form-grid">
          <div className="form-section">
            <h4 className="section-title">Información Básica</h4>
            <div className="form-field">
              <label className="form-label">Título <span className="required-indicator">*</span></label>
              <input
                type="text"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Casa moderna en zona residencial"
              />
            </div>
            <div className="form-field">
              <label className="form-label">Descripción <span className="required-indicator">*</span></label>
              <textarea
                name="descripcion"
                value={formData.descripcion}
                onChange={handleChange}
                required
                className="form-textarea"
                placeholder="Describe las características principales de la propiedad..."
                rows={4}
              />
            </div>
            <div className="form-field">
              <label className="form-label">Dirección <span className="required-indicator">*</span></label>
              <input
                type="text"
                name="direccion"
                value={formData.direccion}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Av. Principal 123, Colonia Centro"
              />
            </div>
            <div className="form-field">
              <label className="form-label">Tipo <span className="required-indicator">*</span></label>
              <select
                name="tipo"
                value={formData.tipo}
                onChange={handleChange}
                required
                className="form-select"
              >
                <option value="">Seleccionar tipo</option>
                {tiposPropiedad.map(tipo => (
                  <option key={tipo} value={tipo}>{tipo}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="form-section">
            <h4 className="section-title">Detalles Numéricos</h4>
            <div className="form-field">
              <label className="form-label">Precio <span className="required-indicator">*</span></label>
              <input
                type="number"
                name="precio"
                value={formData.precio}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>
            <div className="form-row">
              <div className="form-field half-width">
                <label className="form-label">Habitaciones <span className="required-indicator">*</span></label>
                <input
                  type="number"
                  name="habitaciones"
                  value={formData.habitaciones}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>
              <div className="form-field half-width">
                <label className="form-label">Baños <span className="required-indicator">*</span></label>
                <input
                  type="number"
                  name="banos"
                  value={formData.banos}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>
            </div>
            <div className="form-field">
              <label className="form-label">Metros Cuadrados <span className="required-indicator">*</span></label>
              <input
                type="number"
                name="metrosCuadrados"
                value={formData.metrosCuadrados}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>
            <div className="checkbox-field">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  name="disponible"
                  checked={formData.disponible}
                  onChange={handleChange}
                  className="checkbox-input"
                />
                <span className="checkbox-text">Propiedad Disponible</span>
              </label>
            </div>
          </div>
        </div>

        <div className="form-section full-width">
          <h4 className="section-title">Imagen de la Propiedad</h4>
          <div className="form-field">
            <label className="form-label">Seleccionar Nueva Imagen</label>
            <input
              type="file"
              name="imagen"
              accept="image/*"
              onChange={handleImageChange}
              className="form-input-file"
            />
            {imagenPreview && (
              <div className="image-preview-container">
                <p>Previsualización Actual:</p>
                <img src={imagenPreview} alt="Previsualización" className="image-preview" />
              </div>
            )}
            <p className="field-hint">
              Sube una nueva imagen para reemplazar la actual, o déjalo vacío para mantener la imagen existente.
              {(!propiedad.imagenes || propiedad.imagenes.length === 0) && " Esta propiedad no tiene imagen, por favor sube una nueva."}
            </p>
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={onCancel}
            className="btn-secondary"
            disabled={isSubmitting}
          >
            Cancelar
          </button>
          <button
            type="submit"
            className={`btn-primary ${isSubmitting ? 'loading' : ''}`}
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="loading-spinner"></span>
                Actualizando...
              </>
            ) : (
              'Actualizar Propiedad'
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default PropiedadEditForm;
