import axios from 'axios';

const API_URL = 'http://localhost:3000/propiedades';

function PropiedadForm({ propiedad, onSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: '',
    tipo: '',
    habitaciones: '',
    banos: '',
    metrosCuadrados: '',
    disponible: true,
  });
  const [imagenArchivo, setImagenArchivo] = useState(null); // Nuevo estado para el archivo de imagen
  const [imagenPreview, setImagenPreview] = useState(''); // Estado para la previsualización de la imagen
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio !== undefined ? String(propiedad.precio) : '',
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones !== undefined ? String(propiedad.habitaciones) : '',
        banos: propiedad.banos !== undefined ? String(propiedad.banos) : '',
        metrosCuadrados: propiedad.metrosCuadrados !== undefined ? String(propiedad.metrosCuadrados) : '',
        disponible: propiedad.disponible !== undefined ? propiedad.disponible : true,
      });
      // Si la propiedad ya tiene una imagen, la cargamos para previsualización
      if (propiedad.imagenes && propiedad.imagenes.length > 0) {
        setImagenPreview(`http://localhost:3000${propiedad.imagenes[0]}`);
      } else {
        setImagenPreview(''); // Limpiar previsualización si no hay imagen
      }
      setImagenArchivo(null); // Limpiar archivo seleccionado
    } else {
      setFormData({
        titulo: '',
        descripcion: '',
        direccion: '',
        precio: '',
        tipo: '',
        habitaciones: '',
        banos: '',
        metrosCuadrados: '',
        disponible: true,
      });
      setImagenArchivo(null);
      setImagenPreview('');
    }
    setError(null);
  }, [propiedad]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
    if (error) setError(null);
  };

  // Nuevo manejador para el input de tipo 'file'
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImagenArchivo(file);
      // Crea una URL de objeto para previsualizar la imagen
      setImagenPreview(URL.createObjectURL(file));
      setError(null); // Limpia errores si se selecciona un archivo
    } else {
      setImagenArchivo(null);
      setImagenPreview('');
    }
  };

  const validateForm = () => {
    const requiredFields = ['titulo', 'descripcion', 'direccion', 'precio', 'tipo', 'habitaciones', 'banos', 'metrosCuadrados'];
    for (let field of requiredFields) {
      if (!formData[field] || formData[field].toString().trim() === '') {
        return `El campo "${field}" es requerido.`;
      }
    }

    const precio = parseFloat(formData.precio);
    if (isNaN(precio) || precio <= 0) {
      return 'El precio debe ser un número válido mayor a 0.';
    }

    const habitaciones = parseInt(formData.habitaciones);
    if (isNaN(habitaciones) || habitaciones < 0) {
      return 'El número de habitaciones debe ser un número entero no negativo.';
    }

    const banos = parseInt(formData.banos);
    if (isNaN(banos) || banos < 0) {
      return 'El número de baños debe ser un número entero no negativo.';
    }

    const metrosCuadrados = parseFloat(formData.metrosCuadrados);
    if (isNaN(metrosCuadrados) || metrosCuadrados <= 0) {
      return 'Los metros cuadrados deben ser un número válido mayor a 0.';
    }

    // En modo creación, la imagen es obligatoria
    if (!propiedad && !imagenArchivo) {
      return 'Debes subir una imagen para la nueva propiedad.';
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setIsSubmitting(true);

    // Usa FormData para enviar archivos junto con datos de texto
    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    // Añade el archivo de imagen si existe
    if (imagenArchivo) {
      formDataToSend.append('imagen', imagenArchivo);
    } else if (propiedad && propiedad.imagenes && propiedad.imagenes.length > 0) {
      // Si no se subió una nueva imagen pero estamos editando y ya había una imagen,
      // la enviamos de vuelta al backend (en este caso, como un string del array).
      // El backend la interpretará como la imagen existente.
      formDataToSend.append('imagenes', propiedad.imagenes.join(','));
    }


    try {
      if (propiedad) {
        await axios.put(`${API_URL}/${propiedad._id}`, formDataToSend, {
          headers: {
            'Content-Type': 'multipart/form-data', // Importante para enviar FormData
          },
        });
      } else {
        await axios.post(API_URL, formDataToSend, {
          headers: {
            'Content-Type': 'multipart/form-data', // Importante para enviar FormData
          },
        });
      }
      onSuccess();
    } catch (err) {
      console.error('Error al enviar el formulario:', err.response ? err.response.data : err.message);
      setError(
        err.response?.data?.error ||
        err.response?.data?.message ||
        'Ocurrió un error inesperado al guardar la propiedad. Por favor, inténtalo de nuevo.'
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  const tiposPropiedad = [
    'Casa', 'Departamento', 'Oficina', 'Local Comercial',
    'Terreno', 'Bodega', 'Penthouse', 'Duplex', 'Estudio', 'Nave Industrial'
  ];

  return (
    <div className="propiedad-form-container">
      <div className="form-header">
        <h3 className="form-title">
          {propiedad ? 'Editar Propiedad' : 'Nueva Propiedad'}
        </h3>
        <p className="form-subtitle">
          {propiedad
            ? 'Modifica los datos de la propiedad seleccionada.'
            : 'Completa la información para registrar una nueva propiedad.'
          }
        </p>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-icon">⚠</div>
          <div className="error-text">{error}</div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="propiedad-form">
        <div className="form-grid">
          <div className="form-section">
            <h4 className="section-title">Información Básica</h4>

            <div className="form-field">
              <label className="form-label">
                Título <span className="required-indicator">*</span>
              </label>
              <input
                type="text"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Casa moderna en zona residencial"
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Descripción <span className="required-indicator">*</span>
              </label>
              <textarea
                name="descripcion"
                value={formData.descripcion}
                onChange={handleChange}
                required
                className="form-textarea"
                placeholder="Describe las características principales de la propiedad..."
                rows={4}
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Dirección <span className="required-indicator">*</span>
              </label>
              <input
                type="text"
                name="direccion"
                value={formData.direccion}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Av. Principal 123, Colonia Centro"
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Tipo <span className="required-indicator">*</span>
              </label>
              <select
                name="tipo"
                value={formData.tipo}
                onChange={handleChange}
                required
                className="form-select"
              >
                <option value="">Seleccionar tipo</option>
                {tiposPropiedad.map(tipo => (
                  <option key={tipo} value={tipo}>{tipo}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="form-section">
            <h4 className="section-title">Detalles Numéricos</h4>

            <div className="form-field">
              <label className="form-label">
                Precio <span className="required-indicator">*</span>
              </label>
              <input
                type="number"
                name="precio"
                value={formData.precio}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>

            <div className="form-row">
              <div className="form-field half-width">
                <label className="form-label">
                  Habitaciones <span className="required-indicator">*</span>
                </label>
                <input
                  type="number"
                  name="habitaciones"
                  value={formData.habitaciones}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>

              <div className="form-field half-width">
                <label className="form-label">
                  Baños <span className="required-indicator">*</span>
                </label>
                <input
                  type="number"
                  name="banos"
                  value={formData.banos}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>
            </div>

            <div className="form-field">
              <label className="form-label">
                Metros Cuadrados <span className="required-indicator">*</span>
              </label>
              <input
                type="number"
                name="metrosCuadrados"
                value={formData.metrosCuadrados}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>

            <div className="checkbox-field">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  name="disponible"
                  checked={formData.disponible}
                  onChange={handleChange}
                  className="checkbox-input"
                />
                <span className="checkbox-text">Propiedad Disponible</span>
              </label>
            </div>
          </div>
        </div>

        <div className="form-section full-width">
          <h4 className="section-title">Imagen de la Propiedad</h4>
          <div className="form-field">
            <label className="form-label">
              Seleccionar Imagen {propiedad ? '' : <span className="required-indicator">*</span>}
            </label>
            <input
              type="file"
              name="imagen"
              accept="image/*"
              onChange={handleImageChange}
              className="form-input-file"
            />
            {imagenPreview && (
              <div className="image-preview-container">
                <p>Previsualización:</p>
                <img src={imagenPreview} alt="Previsualización" className="image-preview" />
              </div>
            )}
            <p className="field-hint">
              {propiedad ? 'Sube una nueva imagen para reemplazar la actual, o déjalo vacío para mantenerla.' : 'Sube una imagen para la propiedad.'}
            </p>
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={onCancel}
            className="btn-secondary"
            disabled={isSubmitting}
          >
            Cancelar
          </button>
          <button
            type="submit"
            className={`btn-primary ${isSubmitting ? 'loading' : ''}`}
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="loading-spinner"></span>
                Guardando...
              </>
            ) : (
              propiedad ? 'Actualizar Propiedad' : 'Guardar Propiedad'
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default PropiedadForm;
