import React, { useState, useEffect } from 'react';
import './ImageCarousel.css'; // Asegúrate de crear este archivo CSS

const ImageCarousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Configura el deslizamiento automático cada 3 segundos (3000 ms)
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    // Limpia el intervalo cuando el componente se desmonte
    return () => clearInterval(intervalId);
  }, [images.length]); // Se ejecuta cada vez que el número de imágenes cambia

  if (!images || images.length === 0) {
    return null; // No muestra nada si no hay imágenes
  }

  return (
    <div className="carousel-container">
      <img
        src={images[currentIndex]}
        alt={`Propiedad ${currentIndex + 1}`}
        className="carousel-image"
      />
      <div className="carousel-dots">
        {images.map((_, idx) => (
          <span
            key={idx}
            className={`dot ${currentIndex === idx ? 'active' : ''}`}
            onClick={() => setCurrentIndex(idx)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default ImageCarousel;
