import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3000/propiedades'; // Ensure this matches your backend port

function PropiedadForm({ propiedad, onSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: '',
    tipo: '',
    habitaciones: '',
    banos: '',
    metrosCuadrados: '',
    disponible: true,
    imagenes: '', // For simplicity, handle as a comma-separated string
  });
  const [error, setError] = useState(null);

  useEffect(() => {
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio || '',
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones || '',
        banos: propiedad.banos || '',
        metrosCuadrados: propiedad.metrosCuadrados || '',
        disponible: propiedad.disponible || false,
        imagenes: (propiedad.imagenes || []).join(', '), // Convert array to comma-separated string
      });
    } else {
      // Reset form if no propiedad is passed (for adding new)
      setFormData({
        titulo: '',
        descripcion: '',
        direccion: '',
        precio: '',
        tipo: '',
        habitaciones: '',
        banos: '',
        metrosCuadrados: '',
        disponible: true,
        imagenes: '',
      });
    }
    setError(null); // Clear any previous errors
  }, [propiedad]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    const dataToSend = {
      ...formData,
      precio: parseFloat(formData.precio),
      habitaciones: parseInt(formData.habitaciones),
      banos: parseInt(formData.banos),
      metrosCuadrados: parseFloat(formData.metrosCuadrados),
      imagenes: formData.imagenes.split(',').map(img => img.trim()).filter(img => img !== ''), // Convert string to array
    };

    try {
      if (propiedad) {
        // Update existing property
        await axios.put(`${API_URL}/${propiedad._id}`, dataToSend);
      } else {
        // Add new property
        await axios.post(API_URL, dataToSend);
      }
      onSuccess(); // Call success callback to refresh list and hide form
    } catch (err) {
      console.error('Error submitting form:', err.response ? err.response.data : err.message);
      setError(err.response ? err.response.data.error : 'An unexpected error occurred.');
    }
  };

  return (
    <div className="propiedad-form-container">
      <h3>{propiedad ? 'Editar Propiedad' : 'Agregar Nueva Propiedad'}</h3>
      <form onSubmit={handleSubmit} className="propiedad-form">
        {error && <p className="error-message">{error}</p>}
        <div>
          <label>Título:</label>
          <input type="text" name="titulo" value={formData.titulo} onChange={handleChange} required />
        </div>
        <div>
          <label>Descripción:</label>
          <textarea name="descripcion" value={formData.descripcion} onChange={handleChange} required></textarea>
        </div>
        <div>
          <label>Dirección:</label>
          <input type="text" name="direccion" value={formData.direccion} onChange={handleChange} required />
        </div>
        <div>
          <label>Precio:</label>
          <input type="number" name="precio" value={formData.precio} onChange={handleChange} required />
        </div>
        <div>
          <label>Tipo:</label>
          <input type="text" name="tipo" value={formData.tipo} onChange={handleChange} required />
        </div>
        <div>
          <label>Habitaciones:</label>
          <input type="number" name="habitaciones" value={formData.habitaciones} onChange={handleChange} required />
        </div>
        <div>
          <label>Baños:</label>
          <input type="number" name="banos" value={formData.banos} onChange={handleChange} required />
        </div>
        <div>
          <label>Metros Cuadrados:</label>
          <input type="number" name="metrosCuadrados" value={formData.metrosCuadrados} onChange={handleChange} required />
        </div>
        <div>
          <label>
            Disponible:
            <input type="checkbox" name="disponible" checked={formData.disponible} onChange={handleChange} />
          </label>
        </div>
        <div>
          <label>Imágenes (URLs separadas por coma):</label>
          <input type="text" name="imagenes" value={formData.imagenes} onChange={handleChange} placeholder="URL1, URL2, URL3" />
        </div>
        <div className="form-actions">
          <button type="submit">{propiedad ? 'Actualizar' : 'Guardar'}</button>
          <button type="button" onClick={onCancel}>Cancelar</button>
        </div>
      </form>
    </div>
  );
}

export default PropiedadForm;
