import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Asegúrate de que esta URL coincida con el puerto de tu backend
const API_URL = 'http://localhost:3000/propiedades';

function PropiedadForm({ propiedad, onSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: '',
    tipo: '',
    habitaciones: '',
    banos: '',
    metrosCuadrados: '',
    disponible: true,
    imagenes: '', // Mantener como string para la entrada del formulario
  });
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    // Este efecto se ejecuta cada vez que 'propiedad' cambia.
    // Si 'propiedad' es un objeto (modo edición), carga sus datos en el formulario.
    // Si 'propiedad' es null (modo nueva propiedad), reinicia el formulario.
    console.log('PropiedadForm useEffect - propiedad actual:', propiedad); // Para depuración
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio !== undefined ? String(propiedad.precio) : '', // Convertir a string para input type="number"
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones !== undefined ? String(propiedad.habitaciones) : '',
        banos: propiedad.banos !== undefined ? String(propiedad.banos) : '',
        metrosCuadrados: propiedad.metrosCuadrados !== undefined ? String(propiedad.metrosCuadrados) : '',
        disponible: propiedad.disponible !== undefined ? propiedad.disponible : true,
        imagenes: (propiedad.imagenes || []).join(', '), // Unir el array en un string para el input
      });
    } else {
      // Si no hay propiedad (nuevo formulario), reinicia el estado
      setFormData({
        titulo: '',
        descripcion: '',
        direccion: '',
        precio: '',
        tipo: '',
        habitaciones: '',
        banos: '',
        metrosCuadrados: '',
        disponible: true,
        imagenes: '',
      });
    }
    setError(null); // Limpia cualquier error previo al cargar/resetear el formulario
  }, [propiedad]); // Dependencia clave: el efecto se ejecuta cuando 'propiedad' cambia

  // Maneja los cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
    // Limpia el error si el usuario empieza a escribir después de un error de validación
    if (error) setError(null);
  };

  // Valida los campos del formulario antes de enviar
  const validateForm = () => {
    const requiredFields = ['titulo', 'descripcion', 'direccion', 'precio', 'tipo', 'habitaciones', 'banos', 'metrosCuadrados'];
    for (let field of requiredFields) {
      if (!formData[field] || formData[field].toString().trim() === '') {
        return `El campo "${field}" es requerido.`;
      }
    }

    // Validaciones numéricas específicas
    const precio = parseFloat(formData.precio);
    if (isNaN(precio) || precio <= 0) {
      return 'El precio debe ser un número válido mayor a 0.';
    }

    const habitaciones = parseInt(formData.habitaciones);
    if (isNaN(habitaciones) || habitaciones < 0) {
      return 'El número de habitaciones debe ser un número entero no negativo.';
    }

    const banos = parseInt(formData.banos);
    if (isNaN(banos) || banos < 0) {
      return 'El número de baños debe ser un número entero no negativo.';
    }

    const metrosCuadrados = parseFloat(formData.metrosCuadrados);
    if (isNaN(metrosCuadrados) || metrosCuadrados <= 0) {
      return 'Los metros cuadrados deben ser un número válido mayor a 0.';
    }

    return null; // No hay errores de validación
  };

  // Maneja el envío del formulario (crear o actualizar propiedad)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Limpiar errores previos

    // Ejecutar validación
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setIsSubmitting(true); // Indicar que el formulario se está enviando

    // Preparar los datos para enviar al backend
    const dataToSend = {
      ...formData,
      // Convertir valores a tipos numéricos para el backend
      precio: parseFloat(formData.precio),
      habitaciones: parseInt(formData.habitaciones),
      banos: parseInt(formData.banos),
      metrosCuadrados: parseFloat(formData.metrosCuadrados),
      // Convertir el string de imágenes a un array, filtrando entradas vacías
      imagenes: formData.imagenes
        .split(',')
        .map(img => img.trim())
        .filter(img => img !== ''),
    };

    console.log('Datos a enviar al backend:', dataToSend); // Para depuración

    try {
      if (propiedad) {
        // Si hay una propiedad, es una actualización (PUT)
        console.log(`Enviando PUT a: ${API_URL}/${propiedad._id}`); // Para depuración
        await axios.put(`${API_URL}/${propiedad._id}`, dataToSend);
      } else {
        // Si no hay propiedad, es una nueva creación (POST)
        console.log(`Enviando POST a: ${API_URL}`); // Para depuración
        await axios.post(API_URL, dataToSend);
      }
      onSuccess(); // Llamar a la función de éxito (ej. para refrescar la lista en el padre)
    } catch (err) {
      console.error('Error al enviar el formulario:', err.response ? err.response.data : err.message);
      // Mostrar un mensaje de error amigable al usuario
      setError(
        err.response?.data?.error ||
        err.response?.data?.message ||
        'Ocurrió un error inesperado. Por favor, inténtalo de nuevo.'
      );
    } finally {
      setIsSubmitting(false); // Restablecer el estado de envío
    }
  };

  // Opciones predefinidas para el tipo de propiedad
  const tiposPropiedad = [
    'Casa', 'Departamento', 'Oficina', 'Local Comercial',
    'Terreno', 'Bodega', 'Penthouse', 'Duplex', 'Estudio', 'Nave Industrial'
  ];

  return (
    <div className="propiedad-form-container">
      <div className="form-header">
        <h3 className="form-title">
          {propiedad ? 'Editar Propiedad' : 'Nueva Propiedad'}
        </h3>
        <p className="form-subtitle">
          {propiedad
            ? 'Modifica los datos de la propiedad seleccionada.'
            : 'Completa la información para registrar una nueva propiedad.'
          }
        </p>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-icon">⚠</div>
          <div className="error-text">{error}</div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="propiedad-form">
        <div className="form-grid">
          <div className="form-section">
            <h4 className="section-title">Información Básica</h4>

            <div className="form-field">
              <label className="form-label">
                Título <span className="required-indicator">*</span>
              </label>
              <input
                type="text"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Casa moderna en zona residencial"
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Descripción <span className="required-indicator">*</span>
              </label>
              <textarea
                name="descripcion"
                value={formData.descripcion}
                onChange={handleChange}
                required
                className="form-textarea"
                placeholder="Describe las características principales de la propiedad..."
                rows={4}
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Dirección <span className="required-indicator">*</span>
              </label>
              <input
                type="text"
                name="direccion"
                value={formData.direccion}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Av. Principal 123, Colonia Centro"
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Tipo <span className="required-indicator">*</span>
              </label>
              <select
                name="tipo"
                value={formData.tipo}
                onChange={handleChange}
                required
                className="form-select"
              >
                <option value="">Seleccionar tipo</option>
                {tiposPropiedad.map(tipo => (
                  <option key={tipo} value={tipo}>{tipo}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="form-section">
            <h4 className="section-title">Detalles Numéricos</h4>

            <div className="form-field">
              <label className="form-label">
                Precio <span className="required-indicator">*</span>
              </label>
              <input
                type="number"
                name="precio"
                value={formData.precio}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>

            <div className="form-row">
              <div className="form-field half-width">
                <label className="form-label">
                  Habitaciones <span className="required-indicator">*</span>
                </label>
                <input
                  type="number"
                  name="habitaciones"
                  value={formData.habitaciones}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>

              <div className="form-field half-width">
                <label className="form-label">
                  Baños <span className="required-indicator">*</span>
                </label>
                <input
                  type="number"
                  name="banos"
                  value={formData.banos}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>
            </div>

            <div className="form-field">
              <label className="form-label">
                Metros Cuadrados <span className="required-indicator">*</span>
              </label>
              <input
                type="number"
                name="metrosCuadrados"
                value={formData.metrosCuadrados}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>

            <div className="checkbox-field">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  name="disponible"
                  checked={formData.disponible}
                  onChange={handleChange}
                  className="checkbox-input"
                />
                <span className="checkbox-text">Propiedad Disponible</span>
              </label>
            </div>
          </div>
        </div>

        <div className="form-section full-width">
          <h4 className="section-title">Imágenes de la Propiedad</h4>
          <div className="form-field">
            <label className="form-label">URLs de Imágenes</label>
            <input
              type="text"
              name="imagenes"
              value={formData.imagenes}
              onChange={handleChange}
              className="form-input"
              placeholder="https://ejemplo.com/imagen1.jpg, https://ejemplo.com/imagen2.jpg"
            />
            <p className="field-hint">
              Separa múltiples URLs con comas (,)
            </p>
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={onCancel}
            className="btn-secondary"
            disabled={isSubmitting}
          >
            Cancelar
          </button>
          <button
            type="submit"
            className={`btn-primary ${isSubmitting ? 'loading' : ''}`}
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="loading-spinner"></span>
                Guardando...
              </>
            ) : (
              propiedad ? 'Actualizar Propiedad' : 'Guardar Propiedad'
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default PropiedadForm;
