import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PropiedadForm from './PropiedadForm';
import PropiedadEditForm from './PropiedadEditForm';
import PropiedadDetail from './PropiedadDetail';
import AuthForm from './AuthForm';
import './PropiedadesList.css';

const API_URL = 'http://localhost:3000/propiedades';
const API_AUTH_URL = 'http://localhost:3000/api/auth';

function PropiedadesList() {
  const [propiedades, setPropiedades] = useState([]);
  const [editingPropiedad, setEditingPropiedad] = useState(null);
  const [viewingPropiedad, setViewingPropiedad] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAuthForm, setShowAuthForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false); // true para registro, false para login

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
    fetchPropiedades();
  }, []);

  // Interceptor de Axios para añadir el token automáticamente a todas las solicitudes a tu backend
  axios.interceptors.request.use(
    (config) => {
      const token = localStorage.getItem('token');
      // Solo añade el token a las solicitudes dirigidas a tu backend
      if (token && config.url.startsWith('http://localhost:3000/')) {
        config.headers.Authorization = `Bearer ${token}`;
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const fetchPropiedades = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_URL);
      setPropiedades(response.data);
    } catch (err) {
      console.error('Error al obtener propiedades:', err);
      setError('No se pudieron cargar las propiedades. Inténtalo de nuevo más tarde.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!isAuthenticated) {
      alert('Debes iniciar sesión para eliminar propiedades.');
      return;
    }
    if (!window.confirm('¿Estás seguro de que quieres eliminar esta propiedad?')) {
      return;
    }
    try {
      await axios.delete(`${API_URL}/${id}`); // El interceptor añade el token
      fetchPropiedades();
      alert('Propiedad eliminada con éxito.');
    } catch (err) {
      console.error('Error al eliminar propiedad:', err);
      if (err.response && err.response.status === 401) {
        alert('No autorizado. Tu sesión ha expirado o no tienes permisos para realizar esta acción. Por favor, inicia sesión de nuevo.');
        setIsAuthenticated(false);
        localStorage.removeItem('token');
        setShowAuthForm(true);
        setIsRegistering(false); // Asegúrate de que se muestre el login
      } else {
        alert('Hubo un error al eliminar la propiedad. Por favor, inténtalo de nuevo.');
      }
    }
  };

  const handleEdit = (propiedad) => {
    if (!isAuthenticated) {
      alert('Debes iniciar sesión para editar propiedades.');
      return;
    }
    setEditingPropiedad(propiedad);
    setShowEditForm(true);
    setShowCreateForm(false);
    setViewingPropiedad(null);
  };

  const handleAddNew = () => {
    if (!isAuthenticated) {
      alert('Debes iniciar sesión para agregar propiedades.');
      return;
    }
    setEditingPropiedad(null);
    setShowCreateForm(true);
    setShowEditForm(false);
    setViewingPropiedad(null);
  };

  const handleView = (propiedad) => {
    setViewingPropiedad(propiedad);
    setShowCreateForm(false);
    setShowEditForm(false);
    setShowAuthForm(false);
  };

  const handleCloseView = () => {
    setViewingPropiedad(null);
  };

  const handleFormSuccess = () => {
    fetchPropiedades();
    setEditingPropiedad(null);
    setShowCreateForm(false);
    setShowEditForm(false);
    setViewingPropiedad(null);
  };

  const handleFormCancel = () => {
    setEditingPropiedad(null);
    setShowCreateForm(false);
    setShowEditForm(false);
  };

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
    setShowAuthForm(false);
    fetchPropiedades();
  };

  const handleAuthCancel = () => {
    setShowAuthForm(false);
    setIsRegistering(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    alert('Has cerrado sesión exitosamente.');
  };

  // Función para abrir el formulario de registro solo si el usuario ya está logueado
  const handleOpenRegisterForm = () => {
    if (isAuthenticated) {
      setShowAuthForm(true);
      setIsRegistering(true); // Abre el AuthForm en modo registro
    } else {
      // Esto no debería ser accesible si el botón está bien oculto, pero es un fallback
      alert('Solo los usuarios autenticados pueden registrar nuevas cuentas. Por favor, inicia sesión primero.');
      setShowAuthForm(true); // Opcional: muestra el formulario de login para que se autentique
      setIsRegistering(false);
    }
  };

  return (
    <div className="propiedades-list-container">
      <div className="app-header">
        <h1>Gestión de Propiedades</h1>
        <div className="auth-buttons">
          {isAuthenticated ? (
            <>
              <span className="welcome-message">¡Bienvenido!</span>
              {/* Este botón ahora permite registrar nuevos usuarios SOLO si ya estás logueado */}
              <button onClick={handleOpenRegisterForm} className="btn-register-new-user">
                Registrar Nuevo Usuario
              </button>
              <button onClick={handleLogout} className="btn-logout">
                Cerrar Sesión
              </button>
            </>
          ) : (
            <>
              {/* Cuando no está autenticado, solo se muestra el botón de Iniciar Sesión */}
              <button onClick={() => { setShowAuthForm(true); setIsRegistering(false); }} className="btn-login">
                Iniciar Sesión
              </button>
            </>
          )}
        </div>
      </div>

      <div className="list-header">
        <h2>Listado de Propiedades</h2>
        {isAuthenticated && ( // El botón "Agregar Nueva Propiedad" solo se muestra si está autenticado
          <button onClick={handleAddNew} className="btn-add-new">
            Agregar Nueva Propiedad
          </button>
        )}
      </div>

      {showAuthForm && ( // Muestra el formulario de autenticación/registro
        <AuthForm
          onAuthSuccess={handleAuthSuccess} // Para login
          onCancel={handleAuthCancel} // Para cancelar o cerrar el registro
          isRegister={isRegistering} // Controla si es modo registro o login
        />
      )}

      {showCreateForm && isAuthenticated && ( // El formulario de creación solo se muestra si está autenticado
        <PropiedadForm
          onSuccess={handleFormSuccess}
          onCancel={handleFormCancel}
        />
      )}

      {showEditForm && editingPropiedad && isAuthenticated && ( // El formulario de edición solo se muestra si está autenticado
        <PropiedadEditForm
          propiedad={editingPropiedad}
          onSuccess={handleFormSuccess}
          onCancel={handleFormCancel}
        />
      )}

      {viewingPropiedad && ( // La vista de detalles es pública
        <PropiedadDetail
          propiedad={viewingPropiedad}
          onClose={handleCloseView}
        />
      )}

      {loading && <p className="loading-message">Cargando propiedades...</p>}
      {error && <p className="error-message">{error}</p>}

      {/* Mensaje cuando no hay propiedades y no hay formularios abiertos */}
      {!loading && !error && propiedades.length === 0 && !showCreateForm && !showEditForm && !viewingPropiedad && !showAuthForm && (
        <p className="no-propiedades-message">No hay propiedades disponibles. {isAuthenticated ? '¡Agrega una nueva!' : 'Inicia sesión para gestionar propiedades.'}</p>
      )}

      {/* Lista de propiedades solo si no hay formularios/vistas abiertos */}
      {!loading && !error && propiedades.length > 0 && !showCreateForm && !showEditForm && !viewingPropiedad && !showAuthForm && (
        <ul className="propiedades-grid">
          {propiedades.map((propiedad) => (
            <li
              key={propiedad._id}
              className="propiedad-card"
              onClick={() => handleView(propiedad)}
              style={{ cursor: 'pointer' }}
            >
              <div className="card-image-container">
                {propiedad.imagenes && propiedad.imagenes.length > 0 ? (
                  <img
                    src={`http://localhost:3000${propiedad.imagenes[0]}`}
                    alt={propiedad.titulo}
                    className="propiedad-image"
                    onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/200x150?text=No+Image" }}
                  />
                ) : (
                  <img
                    src="https://via.placeholder.com/200x150?text=No+Image"
                    alt="No disponible"
                    className="propiedad-image"
                  />
                )}
              </div>
              <div className="card-content">
                <h3 className="card-title">{propiedad.titulo}</h3>
                <p className="card-detail"><strong>Dirección:</strong> {propiedad.direccion}</p>
                <p className="card-detail"><strong>Precio:</strong> ${propiedad.precio ? propiedad.precio.toLocaleString('es-AR') : 'N/A'}</p>
                <p className="card-detail"><strong>Tipo:</strong> {propiedad.tipo}</p>
                <p className="card-detail"><strong>Habitaciones:</strong> {propiedad.habitaciones} | <strong>Baños:</strong> {propiedad.banos}</p>
                <p className="card-detail"><strong>Metros Cuadrados:</strong> {propiedad.metrosCuadrados}m²</p>
                <p className="card-availability">
                  <strong>Disponible:</strong> {propiedad.disponible ? 'Sí' : 'No'}
                </p>
                {isAuthenticated && ( // Los botones de acción solo se muestran si está autenticado
                  <div className="propiedad-actions" onClick={(e) => e.stopPropagation()}>
                    <button onClick={() => handleEdit(propiedad)} className="btn-edit">
                      Editar
                    </button>
                    <button onClick={() => handleDelete(propiedad._id)} className="btn-delete">
                      Eliminar
                    </button>
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PropiedadesList;
