import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import PropiedadForm from './PropiedadForm'; // Asegúrate de que esta ruta sea correcta
import PropiedadEditForm from './PropiedadEditForm'; // Asegúrate de que esta ruta sea correcta
import PropiedadDetail from './PropiedadDetail'; // Asegúrate de que esta ruta sea correcta

const API_URL = 'http://localhost:3000/propiedades';

function PropiedadesList({ isAuthenticated, setShowAuthForm, setIsRegistering }) {
  const [propiedades, setPropiedades] = useState([]);
  const [editingPropiedad, setEditingPropiedad] = useState(null);
  const [viewingPropiedad, setViewingPropiedad] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Estados para los filtros
  const [filterTipo, setFilterTipo] = useState('');
  const [filterTransaccion, setFilterTransaccion] = useState('');

  // Utilizamos useCallback para memoizar fetchPropiedades.
  // Esto previene que la función se recree en cada render si sus dependencias no cambian,
  // lo que es importante porque es una dependencia del useEffect.
  const fetchPropiedades = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const params = {};
      // Solo añade el parámetro si el filtro tiene un valor seleccionado
      if (filterTipo) {
        params.tipo = filterTipo;
      }
      if (filterTransaccion) {
        params.transaccion = filterTransaccion;
      }

      // La solicitud GET incluye los parámetros de consulta
      const response = await axios.get(API_URL, { params });
      setPropiedades(response.data);
    } catch (err) {
      console.error('Error al obtener propiedades:', err);
      setError('No se pudieron cargar las propiedades. Inténtalo de nuevo más tarde.');
    } finally {
      setLoading(false);
    }
  }, [filterTipo, filterTransaccion]); // Las dependencias de useCallback aseguran que la función se actualice solo si los filtros cambian.

  // Este useEffect se ejecuta cuando el componente se monta y cada vez que fetchPropiedades cambia.
  // Dado que fetchPropiedades solo cambia cuando filterTipo o filterTransaccion cambian,
  // esto asegura que las propiedades se recarguen con los nuevos filtros.
  useEffect(() => {
    fetchPropiedades();
  }, [fetchPropiedades]); // Importante incluir fetchPropiedades en las dependencias.

  // Interceptor para añadir el token de autenticación a las solicitudes
  axios.interceptors.request.use(
    (config) => {
      const token = localStorage.getItem('token');
      if (token && config.url.startsWith('http://localhost:3000/')) {
        config.headers.Authorization = `Bearer ${token}`;
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const handleDelete = async (id) => {
    if (!isAuthenticated) {
      alert('Debes iniciar sesión para eliminar propiedades.');
      return;
    }
    if (!window.confirm('¿Estás seguro de que quieres eliminar esta propiedad?')) {
      return;
    }
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchPropiedades(); // Recargar propiedades después de eliminar
      alert('Propiedad eliminada con éxito.');
    } catch (err) {
      console.error('Error al eliminar propiedad:', err);
      if (err.response && err.response.status === 401) {
        alert('No autorizado. Tu sesión ha expirado o no tienes permisos para realizar esta acción. Por favor, inicia sesión de nuevo.');
        localStorage.removeItem('token');
        setShowAuthForm(true);
        setIsRegistering(false);
      } else {
        alert('Hubo un error al eliminar la propiedad. Por favor, inténtalo de nuevo.');
      }
    }
  };

  const handleEdit = (propiedad) => {
    if (!isAuthenticated) {
      alert('Debes iniciar sesión para editar propiedades.');
      return;
    }
    setEditingPropiedad(propiedad);
    setShowEditForm(true);
    setShowCreateForm(false);
    setViewingPropiedad(null);
  };

  const handleAddNew = () => {
    if (!isAuthenticated) {
      alert('Debes iniciar sesión para agregar propiedades.');
      return;
    }
    setEditingPropiedad(null);
    setShowCreateForm(true);
    setShowEditForm(false);
    setViewingPropiedad(null);
  };

  const handleView = (propiedad) => {
    setViewingPropiedad(propiedad);
    setShowCreateForm(false);
    setShowEditForm(false);
    // setShowAuthForm(false); // Esta línea no es necesaria aquí y podría causar comportamiento inesperado
  };

  const handleCloseView = () => {
    setViewingPropiedad(null);
  };

  const handleFormSuccess = () => {
      // Limpiamos los filtros al añadir o editar con éxito, y luego recargamos
      setFilterTipo('');
      setFilterTransaccion('');
      // fetchPropiedades se llamará automáticamente debido a la actualización de los estados
      setEditingPropiedad(null);
      setShowCreateForm(false);
      setShowEditForm(false);
      setViewingPropiedad(null);
  };

  const handleFormCancel = () => {
    setEditingPropiedad(null);
    setShowCreateForm(false);
    setShowEditForm(false);
  };

  // Listas de opciones para los filtros
  const tiposPropiedad = [
    'Casa', 'Departamento', 'Oficina', 'Local Comercial',
    'Terreno', 'Bodega', 'Penthouse', 'Duplex', 'Estudio', 'Nave Industrial'
  ];
  const tiposTransaccion = ['Venta', 'Alquiler', 'Alquiler Temporario'];

  // Función para limpiar ambos filtros
  const handleClearFilters = () => {
    setFilterTipo('');
    setFilterTransaccion('');
    // fetchPropiedades se llamará automáticamente debido a la actualización de los estados
  };

  return (
    <div className="propiedades-list-container">
      <div className="list-header">
        <h2>Listado de Propiedades</h2>
        {isAuthenticated && (
          <button onClick={handleAddNew} className="btn-add-new">
            Agregar Nueva Propiedad
          </button>
        )}
      </div>

      {/* Sección de Filtros */}
      <div className="filter-section">
        <div className="filter-group">
          <label htmlFor="filterTipo">Filtrar por Tipo:</label>
          <select
            id="filterTipo"
            value={filterTipo}
            onChange={(e) => setFilterTipo(e.target.value)}
            className="form-select"
          >
            <option value="">Todos los Tipos</option>
            {tiposPropiedad.map((tipo) => (
              <option key={tipo} value={tipo}>{tipo}</option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="filterTransaccion">Filtrar por Transacción:</label>
          <select
            id="filterTransaccion"
            value={filterTransaccion}
            onChange={(e) => setFilterTransaccion(e.target.value)}
            className="form-select"
          >
            <option value="">Todas las Transacciones</option>
            {tiposTransaccion.map((transaccion) => (
              <option key={transaccion} value={transaccion}>{transaccion}</option>
            ))}
          </select>
        </div>

        <button onClick={handleClearFilters} className="btn-secondary">
          Limpiar Filtros
        </button>
      </div>

      {/* Formularios y Vista de Detalle */}
      {showCreateForm && isAuthenticated && (
        <PropiedadForm
          onSuccess={handleFormSuccess}
          onCancel={handleFormCancel}
        />
      )}

      {showEditForm && editingPropiedad && isAuthenticated && (
        <PropiedadEditForm
          propiedad={editingPropiedad}
          onSuccess={handleFormSuccess}
          onCancel={handleFormCancel}
        />
      )}

      {viewingPropiedad && (
        <PropiedadDetail
          propiedad={viewingPropiedad}
          onClose={handleCloseView}
        />
      )}

      {/* Mensajes de Estado */}
      {loading && <p className="loading-message">Cargando propiedades...</p>}
      {error && <p className="error-message">{error}</p>}

      {!loading && !error && propiedades.length === 0 && !showCreateForm && !showEditForm && !viewingPropiedad && (
        <p className="no-propiedades-message">No hay propiedades disponibles con los filtros actuales. {isAuthenticated ? '¡Intenta agregar una nueva o limpiar los filtros!' : 'Inicia sesión para gestionar propiedades.'}</p>
      )}

      {/* Lista de Propiedades */}
      {!loading && !error && propiedades.length > 0 && !showCreateForm && !showEditForm && !viewingPropiedad && (
        <ul className="propiedades-grid">
          {propiedades.map((propiedad) => (
            <li
              key={propiedad._id}
              className="propiedad-card"
              onClick={() => handleView(propiedad)}
              style={{ cursor: 'pointer' }}
            >
              <div className="card-image-container">
                {propiedad.imagenes && propiedad.imagenes.length > 0 ? (
                  <img
                    src={`http://localhost:3000${propiedad.imagenes[0]}`}
                    alt={propiedad.titulo}
                    className="propiedad-image"
                    onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/400x250?text=Imagen+No+Disponible" }}
                  />
                ) : (
                  <img
                    src="https://via.placeholder.com/400x250?text=Imagen+No+Disponible"
                    alt="No disponible"
                    className="propiedad-image"
                  />
                )}
              </div>
              <div className="card-content">
                <h3 className="card-title">{propiedad.titulo}</h3>
                <p className="card-detail"><strong>Transacción:</strong> {propiedad.transaccion || 'N/A'}</p>
                <p className="card-detail"><strong>Dirección:</strong> {propiedad.direccion}</p>
                <p className="card-detail"><strong>Precio:</strong> ${propiedad.precio ? propiedad.precio.toLocaleString('es-AR') : 'N/A'}</p>
                <p className="card-detail"><strong>Tipo:</strong> {propiedad.tipo}</p>
                <p className="card-detail"><strong>Habitaciones:</strong> {propiedad.habitaciones} | <strong>Baños:</strong> {propiedad.banos}</p>
                <p className="card-detail"><strong>Metros Cuadrados:</strong> {propiedad.metrosCuadrados}m²</p>
                <p className="card-availability">
                  <strong>Disponible:</strong> {propiedad.disponible ? 'Sí' : 'No'}
                </p>
                {isAuthenticated && (
                  <div className="propiedad-actions" onClick={(e) => e.stopPropagation()}>
                    <button onClick={() => handleEdit(propiedad)} className="btn-edit">
                      Editar
                    </button>
                    <button onClick={() => handleDelete(propiedad._id)} className="btn-delete">
                      Eliminar
                    </button>
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PropiedadesList;
