import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Input,
  Textarea,
  Button,
  Switch,
  VStack,
  HStack,
  Box,
} from '@chakra-ui/react';
import { useToast } from '@chakra-ui/toast'; // Correct import for useToast
import {
  FormControl,
  FormLabel,
  FormErrorMessage,
} from '@chakra-ui/form-control'; // Correct imports for form controls
import {
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
} from '@chakra-ui/number-input'; // Correct imports for number input

const API_URL = 'http://localhost:3000/propiedades'; // Make sure this matches your backend port

function PropiedadForm({ propiedad, onSuccess, onCancel }) {
  const toast = useToast();
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: 0,
    tipo: '',
    habitaciones: 0,
    banos: 0,
    metrosCuadrados: 0,
    disponible: true,
    imagenes: '', // For simplicity, handle as a comma-separated string
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio || 0,
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones || 0,
        banos: propiedad.banos || 0,
        metrosCuadrados: propiedad.metrosCuadrados || 0,
        disponible: propiedad.disponible || false,
        imagenes: (propiedad.imagenes || []).join(', '), // Convert array to comma-separated string
      });
    } else {
      // Reset form if no propiedad is passed (for adding new)
      setFormData({
        titulo: '',
        descripcion: '',
        direccion: '',
        precio: 0,
        tipo: '',
        habitaciones: 0,
        banos: 0,
        metrosCuadrados: 0,
        disponible: true,
        imagenes: '',
      });
    }
    setErrors({}); // Clear any previous errors
  }, [propiedad]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleNumberChange = (name, valueAsString, valueAsNumber) => {
    setFormData((prevData) => ({
      ...prevData,
      [name]: valueAsNumber,
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.titulo) newErrors.titulo = 'El título es requerido.';
    if (!formData.descripcion) newErrors.descripcion = 'La descripción es requerida.';
    if (!formData.direccion) newErrors.direccion = 'La dirección es requerida.';
    if (formData.precio <= 0) newErrors.precio = 'El precio debe ser mayor a 0.';
    if (!formData.tipo) newErrors.tipo = 'El tipo es requerido.';
    if (formData.habitaciones <= 0) newErrors.habitaciones = 'Las habitaciones deben ser mayor a 0.';
    if (formData.banos < 0) newErrors.banos = 'Los baños no pueden ser negativos.';
    if (formData.metrosCuadrados <= 0) newErrors.metrosCuadrados = 'Los metros cuadrados deben ser mayor a 0.';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      toast({
        title: 'Formulario inválido.',
        description: 'Por favor, corrige los errores.',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    setIsSubmitting(true);
    const dataToSend = {
      ...formData,
      precio: parseFloat(formData.precio),
      habitaciones: parseInt(formData.habitaciones),
      banos: parseInt(formData.banos),
      metrosCuadrados: parseFloat(formData.metrosCuadrados),
      imagenes: formData.imagenes.split(',').map(img => img.trim()).filter(img => img !== ''), // Convert string to array
    };

    try {
      if (propiedad) {
        await axios.put(`${API_URL}/${propiedad._id}`, dataToSend);
        toast({
          title: 'Propiedad actualizada.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        await axios.post(API_URL, dataToSend);
        toast({
          title: 'Propiedad agregada.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      }
      onSuccess(); // Call success callback to refresh list and hide form
    } catch (err) {
      console.error('Error submitting form:', err.response ? err.response.data : err.message);
      setErrors({ api: err.response?.data?.error || 'Error al guardar la propiedad.' });
      toast({
        title: 'Error.',
        description: err.response?.data?.error || 'Ocurrió un error inesperado al guardar la propiedad.',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Box p={4} borderRadius="md" bg="white">
      <form onSubmit={handleSubmit}>
        <VStack spacing={4}>
          {errors.api && (
            <Box bg="red.100" p={3} borderRadius="md" width="100%" textAlign="center" color="red.700">
              {errors.api}
            </Box>
          )}

          <FormControl id="titulo" isRequired isInvalid={!!errors.titulo}>
            <FormLabel>Título</FormLabel>
            <Input name="titulo" value={formData.titulo} onChange={handleChange} placeholder="Ej: Hermoso apartamento céntrico" />
            <FormErrorMessage>{errors.titulo}</FormErrorMessage>
          </FormControl>

          <FormControl id="descripcion" isRequired isInvalid={!!errors.descripcion}>
            <FormLabel>Descripción</FormLabel>
            <Textarea name="descripcion" value={formData.descripcion} onChange={handleChange} placeholder="Describe la propiedad en detalle..." />
            <FormErrorMessage>{errors.descripcion}</FormErrorMessage>
          </FormControl>

          <FormControl id="direccion" isRequired isInvalid={!!errors.direccion}>
            <FormLabel>Dirección</FormLabel>
            <Input name="direccion" value={formData.direccion} onChange={handleChange} placeholder="Ej: Calle Falsa 123, Ciudad" />
            <FormErrorMessage>{errors.direccion}</FormErrorMessage>
          </FormControl>

          <FormControl id="precio" isRequired isInvalid={!!errors.precio}>
            <FormLabel>Precio</FormLabel>
            <NumberInput value={formData.precio} min={0} onChange={(valAsString, valAsNumber) => handleNumberChange('precio', valAsString, valAsNumber)}>
              <NumberInputField />
              <NumberInputStepper>
                <NumberIncrementStepper />
                <NumberDecrementStepper />
              </NumberInputStepper>
            </NumberInput>
            <FormErrorMessage>{errors.precio}</FormErrorMessage>
          </FormControl>

          <FormControl id="tipo" isRequired isInvalid={!!errors.tipo}>
            <FormLabel>Tipo</FormLabel>
            <Input name="tipo" value={formData.tipo} onChange={handleChange} placeholder="Ej: Apartamento, Casa, Oficina" />
            <FormErrorMessage>{errors.tipo}</FormErrorMessage>
          </FormControl>

          <FormControl id="habitaciones" isRequired isInvalid={!!errors.habitaciones}>
            <FormLabel>Habitaciones</FormLabel>
            <NumberInput value={formData.habitaciones} min={0} onChange={(valAsString, valAsNumber) => handleNumberChange('habitaciones', valAsString, valAsNumber)}>
              <NumberInputField />
              <NumberInputStepper>
                <NumberIncrementStepper />
                <NumberDecrementStepper />
              </NumberInputStepper>
            </NumberInput>
            <FormErrorMessage>{errors.habitaciones}</FormErrorMessage>
          </FormControl>

          <FormControl id="banos" isRequired isInvalid={!!errors.banos}>
            <FormLabel>Baños</FormLabel>
            <NumberInput value={formData.banos} min={0} onChange={(valAsString, valAsNumber) => handleNumberChange('banos', valAsString, valAsNumber)}>
              <NumberInputField />
              <NumberInputStepper>
                <NumberIncrementStepper />
                <NumberDecrementStepper />
              </NumberInputStepper>
            </NumberInput>
            <FormErrorMessage>{errors.banos}</FormErrorMessage>
          </FormControl>

          <FormControl id="metrosCuadrados" isRequired isInvalid={!!errors.metrosCuadrados}>
            <FormLabel>Metros Cuadrados (m²)</FormLabel>
            <NumberInput value={formData.metrosCuadrados} min={0} onChange={(valAsString, valAsNumber) => handleNumberChange('metrosCuadrados', valAsString, valAsNumber)}>
              <NumberInputField />
              <NumberInputStepper>
                <NumberIncrementStepper />
                <NumberDecrementStepper />
              </NumberInputStepper>
            </NumberInput>
            <FormErrorMessage>{errors.metrosCuadrados}</FormErrorMessage>
          </FormControl>

          <FormControl display="flex" alignItems="center" id="disponible">
            <FormLabel htmlFor="disponible-switch" mb="0">
              Disponible
            </FormLabel>
            <Switch
              name="disponible"
              id="disponible-switch"
              isChecked={formData.disponible}
              onChange={handleChange}
              colorScheme="teal"
            />
          </FormControl>

          <FormControl id="imagenes">
            <FormLabel>Imágenes (URLs separadas por coma)</FormLabel>
            <Input
              name="imagenes"
              value={formData.imagenes}
              onChange={handleChange}
              placeholder="URL1, URL2, URL3"
            />
          </FormControl>

          <HStack spacing={4} justifyContent="flex-end" width="100%">
            <Button onClick={onCancel} variant="outline">
              Cancelar
            </Button>
            <Button colorScheme="brand" type="submit" isLoading={isSubmitting}>
              {propiedad ? 'Actualizar Propiedad' : 'Guardar Propiedad'}
            </Button>
          </HStack>
        </VStack>
      </form>
    </Box>
  );
}

export default PropiedadForm;
