import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PropiedadForm from './PropiedadForm'; // Asegúrate de que la ruta sea correcta

// Asegúrate de que esta URL coincida con el puerto de tu backend
const API_URL = 'http://localhost:3000/propiedades';

function PropiedadesList() {
  const [propiedades, setPropiedades] = useState([]);
  const [editingPropiedad, setEditingPropiedad] = useState(null); // Guarda la propiedad a editar
  const [showForm, setShowForm] = useState(false); // Controla la visibilidad del formulario
  const [loading, setLoading] = useState(true); // Para el estado de carga inicial
  const [error, setError] = useState(null); // Para manejar errores al cargar propiedades

  // Efecto para cargar las propiedades al montar el componente
  useEffect(() => {
    fetchPropiedades();
  }, []);

  // Función para obtener las propiedades del backend
  const fetchPropiedades = async () => {
    setLoading(true); // Indica que la carga ha comenzado
    setError(null); // Limpia cualquier error previo
    try {
      const response = await axios.get(API_URL);
      setPropiedades(response.data);
    } catch (err) {
      console.error('Error al obtener propiedades:', err);
      setError('No se pudieron cargar las propiedades. Inténtalo de nuevo más tarde.');
    } finally {
      setLoading(false); // Indica que la carga ha terminado
    }
  };

  // Maneja la eliminación de una propiedad
  const handleDelete = async (id) => {
    if (!window.confirm('¿Estás seguro de que quieres eliminar esta propiedad?')) {
      return; // Si el usuario cancela, no hacer nada
    }
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchPropiedades(); // Refresca la lista después de eliminar
    } catch (err) {
      console.error('Error al eliminar propiedad:', err);
      alert('Hubo un error al eliminar la propiedad. Por favor, inténtalo de nuevo.');
    }
  };

  // Maneja la edición de una propiedad (muestra el formulario con los datos)
  const handleEdit = (propiedad) => {
    setEditingPropiedad(propiedad); // Establece la propiedad que se va a editar
    setShowForm(true); // Muestra el formulario
  };

  // Callback para cuando el formulario de PropiedadForm se envía con éxito
  const handleFormSubmit = () => {
    fetchPropiedades(); // Refresca la lista de propiedades
    setEditingPropiedad(null); // Limpia la propiedad que estaba siendo editada
    setShowForm(false); // Oculta el formulario
  };

  // Callback para cuando el formulario de PropiedadForm se cancela
  const handleCancelForm = () => {
    setEditingPropiedad(null); // Limpia la propiedad de edición
    setShowForm(false); // Oculta el formulario
  };

  return (
    <div className="propiedades-list-container">
      <div className="list-header">
        <h2>Listado de Propiedades</h2>
        <button
          onClick={() => {
            setEditingPropiedad(null); // Asegura que sea un formulario nuevo
            setShowForm(true);
          }}
          className="btn-add-new"
        >
          Agregar Nueva Propiedad
        </button>
      </div>

      {/* Muestra el formulario si showForm es true */}
      {showForm && (
        <PropiedadForm
          propiedad={editingPropiedad} // Pasa la propiedad a editar (o null para nueva)
          onSuccess={handleFormSubmit}
          onCancel={handleCancelForm}
        />
      )}

      {/* Mensajes de estado (carga, error, no hay propiedades) */}
      {loading && <p className="loading-message">Cargando propiedades...</p>}
      {error && <p className="error-message">{error}</p>}

      {!loading && !error && propiedades.length === 0 && (
        <p className="no-propiedades-message">No hay propiedades disponibles. ¡Agrega una nueva!</p>
      )}

      {/* Lista de propiedades */}
      {!loading && !error && propiedades.length > 0 && (
        <ul className="propiedades-grid">
          {propiedades.map((propiedad) => (
            <li key={propiedad._id} className="propiedad-card">
              <div className="card-image-container">
                {propiedad.imagenes && propiedad.imagenes.length > 0 ? (
                  <img
                    src={propiedad.imagenes[0]}
                    alt={propiedad.titulo}
                    className="propiedad-image"
                    onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/200x150?text=No+Image" }} // Fallback de imagen
                  />
                ) : (
                  <img
                    src="https://via.placeholder.com/200x150?text=No+Image"
                    alt="No disponible"
                    className="propiedad-image"
                  />
                )}
              </div>
              <div className="card-content">
                <h3 className="card-title">{propiedad.titulo}</h3>
                <p className="card-detail"><strong>Dirección:</strong> {propiedad.direccion}</p>
                <p className="card-detail"><strong>Precio:</strong> ${propiedad.precio ? propiedad.precio.toLocaleString('es-AR') : 'N/A'}</p> {/* Formato de moneda */}
                <p className="card-detail"><strong>Tipo:</strong> {propiedad.tipo}</p>
                <p className="card-detail"><strong>Habitaciones:</strong> {propiedad.habitaciones} | <strong>Baños:</strong> {propiedad.banos}</p>
                <p className="card-detail"><strong>Metros Cuadrados:</strong> {propiedad.metrosCuadrados}m²</p>
                <p className="card-availability">
                  <strong>Disponible:</strong> {propiedad.disponible ? 'Sí' : 'No'}
                </p>
                <div className="propiedad-actions">
                  <button onClick={() => handleEdit(propiedad)} className="btn-edit">
                    Editar
                  </button>
                  <button onClick={() => handleDelete(propiedad._id)} className="btn-delete">
                    Eliminar
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PropiedadesList;
