import React, { useState, useEffect } from 'react';
import PropiedadesList from './components/PropiedadesList';
import AuthForm from './components/AuthForm';
import ImageCarousel from './components/ImageCarousel'; // Importa el nuevo componente
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAuthForm, setShowAuthForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [carouselImages, setCarouselImages] = useState([]); // Nuevo estado para las imágenes del carrusel

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }

    // Simula la carga de imágenes desde una API
    const fetchImages = async () => {
      try {
        // Aquí harías tu llamada a la API
        // const response = await fetch('TU_URL_DE_API_DE_IMAGENES');
        // const data = await response.json();
        // setCarouselImages(data.images); // Asumiendo que la API devuelve un array de URLs en `data.images`

        // Por ahora, usamos imágenes de ejemplo:
        setCarouselImages([
          'https://via.placeholder.com/800x400?text=Propiedad+1',
          'https://via.placeholder.com/800x400?text=Propiedad+2',
          'https://via.placeholder.com/800x400?text=Propiedad+3',
        ]);
      } catch (error) {
        console.error('Error al cargar imágenes del carrusel:', error);
      }
    };

    fetchImages();
  }, []);

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
    setShowAuthForm(false);
  };

  const handleAuthCancel = () => {
    setShowAuthForm(false);
    setIsRegistering(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    alert('Has cerrado sesión exitosamente.');
  };

  const handleOpenRegisterForm = () => {
    if (isAuthenticated) {
      setShowAuthForm(true);
      setIsRegistering(true);
    } else {
      alert('Solo los usuarios autenticados pueden registrar nuevas cuentas. Por favor, inicia sesión primero.');
      setShowAuthForm(true);
      setIsRegistering(false);
    }
  };

  return (
    <div className="App">
      <header>
        <img src="/logo-inmobiliaria.png" alt="Logo de la Inmobiliaria" className="app-logo" />
        <h1>Gestión de Propiedades</h1>
        <p>Tu plataforma intuitiva para administrar propiedades de manera eficiente.</p>
        <div className="auth-buttons">
          {isAuthenticated ? (
            <>
              <span className="welcome-message">¡Bienvenido!</span>
              <button onClick={handleOpenRegisterForm} className="btn-register-new-user">
                Registrar Nuevo Usuario
              </button>
              <button onClick={handleLogout} className="btn-logout">
                Cerrar Sesión
              </button>
            </>
          ) : (
            <>
              {/* El botón de Iniciar Sesión ahora es más prominente */}
              <button onClick={() => { setShowAuthForm(true); setIsRegistering(false); }} className="btn-login primary-login-btn">
                Iniciar Sesión
              </button>
            </>
          )}
        </div>
      </header>
      <main>
        {/* Aquí integramos el carrusel */}
        {carouselImages.length > 0 && <ImageCarousel images={carouselImages} />}

        <PropiedadesList
          isAuthenticated={isAuthenticated}
          setShowAuthForm={setShowAuthForm}
          setIsRegistering={setIsRegistering}
        />
      </main>

      {showAuthForm && (
        <AuthForm
          onAuthSuccess={handleAuthSuccess}
          onCancel={handleAuthCancel}
          isRegister={isRegistering}
        />
      )}
    </div>
  );
}

export default App;
