import React, { useState, useEffect } from 'react';
import PropiedadesList from './components/PropiedadesList';
import AuthForm from './components/AuthForm';
import ImageCarousel from './components/ImageCarousel.js';
import './App.css';
import logo from './imagenes/logo.png';

// Importa tus imágenes para el carrusel aquí
// Asegúrate de que las rutas y nombres de archivo coincidan con tus imágenes reales
import carouselImage1 from './imagenes/imagen1.jpg'; // Cambia 'imagen1.png' por el nombre de tu archivo
import carouselImage2 from './imagenes/imagen2.jpg'; // Cambia 'imagen2.png' por el nombre de tu archivo
import carouselImage3 from './imagenes/imagen3.jpg'; // Cambia 'imagen3.png' por el nombre de tu archivo

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAuthForm, setShowAuthForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [carouselImages, setCarouselImages] = useState([]);

  // Ya no necesitas la clave de Unsplash ni el fetch, así que puedes quitarlo o comentarlo.
  // const UNSPLASH_ACCESS_KEY = 'TU_ACCES_KEY_DE_UNSPLASH';

  useEffect(() => {
    // Verifica si hay un token de autenticación en el almacenamiento local al cargar la aplicación.
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }

    // Aquí es donde asignas tus imágenes importadas al estado del carrusel.
    setCarouselImages([carouselImage1, carouselImage2, carouselImage3]);

    // La lógica de Unsplash se elimina, por lo tanto, la dependencia UNSPLASH_ACCESS_KEY ya no es necesaria.
  }, []); // El array de dependencias está vacío porque las imágenes son estáticas.

  // Maneja el éxito de la autenticación o registro.
  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
    setShowAuthForm(false); // Cierra el formulario de autenticación/registro.
  };

  // Maneja la cancelación del formulario de autenticación/registro.
  const handleAuthCancel = () => {
    setShowAuthForm(false);
    setIsRegistering(false); // Restablece el estado de registro.
  };

  // Maneja el cierre de sesión del usuario.
  const handleLogout = () => {
    localStorage.removeItem('token'); // Elimina el token del almacenamiento local.
    setIsAuthenticated(false); // Actualiza el estado de autenticación.
    alert('Has cerrado sesión exitosamente.');
  };

  // Maneja la apertura del formulario de registro de un nuevo usuario.
  const handleOpenRegisterForm = () => {
    // Si el usuario ya está autenticado, puede registrar un nuevo usuario.
    if (isAuthenticated) {
      setShowAuthForm(true);
      setIsRegistering(true); // Establece el formulario en modo registro.
    } else {
      // Si el usuario no está autenticado, se le pide que inicie sesión primero.
      alert('Para registrar nuevas cuentas, debes iniciar sesión como un usuario existente.');
      setShowAuthForm(true);
      setIsRegistering(false); // Se muestra el formulario de inicio de sesión.
    }
  };

  return (
    <div className="App">
      <header>
        <img src={logo} alt="Logo de la Inmobiliaria" className="app-logo" />
        <div className="auth-buttons">
          {isAuthenticated ? (
            // Si el usuario está autenticado, se muestran los botones para registrar nuevo usuario y cerrar sesión.
            <>
              <button onClick={handleOpenRegisterForm} className="btn-register-new-user">
                Registrar Nuevo Usuario
              </button>
              <button onClick={handleLogout} className="btn-logout">
                Cerrar Sesión
              </button>
            </>
          ) : (
            // Si el usuario no está autenticado, solo se muestra el botón para iniciar sesión.
            <>
              <button onClick={() => { setShowAuthForm(true); setIsRegistering(false); }} className="btn-login primary-login-btn">
                Iniciar Sesión
              </button>
            </>
          )}
        </div>
      </header>
      <main>
        {/* Renderiza el carrusel de imágenes si hay imágenes cargadas. */}
        {carouselImages.length > 0 && <ImageCarousel images={carouselImages} />}
        {/* Renderiza la lista de propiedades, pasando el estado de autenticación y funciones para mostrar formularios. */}
        <PropiedadesList
          isAuthenticated={isAuthenticated}
          setShowAuthForm={setShowAuthForm}
          setIsRegistering={setIsRegistering}
        />
      </main>

      {/* Renderiza el formulario de autenticación/registro si showAuthForm es verdadero. */}
      {showAuthForm && (
        <AuthForm
          onAuthSuccess={handleAuthSuccess}
          onCancel={handleAuthCancel}
          sRegister={isRegistering}
        />
      )}   
      <footer className="app-footer">
  <div className="footer-content">
    <div className="footer-info">
      <div className="footer-title">
        <i className="fas fa-building"></i>
        <strong>MKalpin Negocios Inmobiliarios</strong>
      </div>
      <div className="footer-address">
        <i className="fas fa-map-marker-alt"></i>
        <span>Florida 142, Piso 8 Oficina I</span>
      </div>
      <div className="footer-city">
        <i className="fas fa-city"></i>
        <span>CABA, Argentina</span>
      </div>
      <div className="footer-contact">
        <i className="fas fa-phone-alt"></i>
        <a href="tel:1156690002">11 5669-0002</a>
      </div>
      <div className="footer-contact">
        <i className="fas fa-envelope"></i>
        <a href="mailto:mkalpinni@gmail.com">mkalpinni@gmail.com</a>
      </div>
    </div>
    <div className="footer-social">
      <span className="footer-social-title">Seguinos</span>
      <div className="footer-social-icons">
        <a href="https://www.facebook.com/" target="_blank" rel="noopener noreferrer" aria-label="Facebook">
          <i className="fab fa-facebook-f"></i>
        </a>
        <a href="https://www.instagram.com/mkalpinni/" target="_blank" rel="noopener noreferrer" aria-label="Instagram">
          <i className="fab fa-instagram"></i>
        </a>
        <a href="https://wa.me/541156690002" target="_blank" rel="noopener noreferrer" aria-label="WhatsApp">
          <i className="fab fa-whatsapp"></i>
        </a>
      </div>
    </div>
  </div>
  <div className="footer-copy">
    © {new Date().getFullYear()} Inmobiliaria ADR. Todos los derechos reservados.
  </div>
</footer>  

    </div>
  );
}

export default App;
