import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3000/propiedades';

function PropiedadForm({ propiedad, onSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    direccion: '',
    precio: '',
    tipo: '',
    habitaciones: '',
    banos: '',
    metrosCuadrados: '',
    disponible: true,
    // No necesitamos 'imagenes' como string en formData para la entrada,
    // lo manejaremos como un array de URLs desde el backend o un array de archivos para el envío.
  });
  const [selectedFiles, setSelectedFiles] = useState([]); // Nuevo estado para los archivos seleccionados
  const [existingImageUrls, setExistingImageUrls] = useState([]); // Para mostrar URLs existentes en modo edición
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (propiedad) {
      setFormData({
        titulo: propiedad.titulo || '',
        descripcion: propiedad.descripcion || '',
        direccion: propiedad.direccion || '',
        precio: propiedad.precio !== undefined ? String(propiedad.precio) : '',
        tipo: propiedad.tipo || '',
        habitaciones: propiedad.habitaciones !== undefined ? String(propiedad.habitaciones) : '',
        banos: propiedad.banos !== undefined ? String(propiedad.banos) : '',
        metrosCuadrados: propiedad.metrosCuadrados !== undefined ? String(propiedad.metrosCuadrados) : '',
        disponible: propiedad.disponible !== undefined ? propiedad.disponible : true,
      });
      // Cargar las URLs existentes para mostrarlas
      setExistingImageUrls(propiedad.imagenes || []);
      setSelectedFiles([]); // Limpiar archivos seleccionados al cargar una propiedad existente
    } else {
      setFormData({
        titulo: '',
        descripcion: '',
        direccion: '',
        precio: '',
        tipo: '',
        habitaciones: '',
        banos: '',
        metrosCuadrados: '',
        disponible: true,
      });
      setExistingImageUrls([]);
      setSelectedFiles([]);
    }
    setError(null);
  }, [propiedad]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
    if (error) setError(null);
  };

  // Nuevo manejador para la selección de archivos
  const handleFileChange = (e) => {
    setSelectedFiles(Array.from(e.target.files)); // Convierte FileList a Array
    if (error) setError(null);
  };

  const validateForm = () => {
    const requiredFields = ['titulo', 'descripcion', 'direccion', 'precio', 'tipo', 'habitaciones', 'banos', 'metrosCuadrados'];
    for (let field of requiredFields) {
      if (!formData[field] || formData[field].toString().trim() === '') {
        return `El campo "${field}" es requerido.`;
      }
    }

    const precio = parseFloat(formData.precio);
    if (isNaN(precio) || precio <= 0) {
      return 'El precio debe ser un número válido mayor a 0.';
    }

    const habitaciones = parseInt(formData.habitaciones);
    if (isNaN(habitaciones) || habitaciones < 0) {
      return 'El número de habitaciones debe ser un número entero no negativo.';
    }

    const banos = parseInt(formData.banos);
    if (isNaN(banos) || banos < 0) {
      return 'El número de baños debe ser un número entero no negativo.';
    }

    const metrosCuadrados = parseFloat(formData.metrosCuadrados);
    if (isNaN(metrosCuadrados) || metrosCuadrados <= 0) {
      return 'Los metros cuadrados deben ser un número válido mayor a 0.';
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setIsSubmitting(true);

    // Creamos un objeto FormData para enviar datos mixtos (texto y archivos)
    const formPayload = new FormData();

    // Añadir todos los campos de texto
    for (const key in formData) {
      formPayload.append(key, formData[key]);
    }

    // Añadir las imágenes existentes (si estamos en modo edición)
    // El backend necesitará saber qué URLs ya están guardadas.
    existingImageUrls.forEach((url, index) => {
      formPayload.append(`existingImages[${index}]`, url);
    });

    // Añadir los nuevos archivos seleccionados
    selectedFiles.forEach((file) => {
      formPayload.append('imagenes', file); // 'imagenes' debe coincidir con el nombre del campo en el backend para Multer
    });

    console.log('Datos a enviar al backend (FormData):');
    // Para ver el contenido de FormData, necesitas iterar sobre él
    for (let pair of formPayload.entries()) {
      console.log(pair[0] + ', ' + pair[1]);
    }


    try {
      if (propiedad) {
        await axios.put(`${API_URL}/${propiedad._id}`, formPayload, {
          headers: {
            'Content-Type': 'multipart/form-data', // MUY IMPORTANTE para FormData
          },
        });
      } else {
        await axios.post(API_URL, formPayload, {
          headers: {
            'Content-Type': 'multipart/form-data', // MUY IMPORTANTE para FormData
          },
        });
      }
      onSuccess();
    } catch (err) {
      console.error('Error al enviar el formulario:', err.response ? err.response.data : err.message);
      setError(
        err.response?.data?.error ||
        err.response?.data?.message ||
        'Ocurrió un error inesperado. Por favor, inténtalo de nuevo.'
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  const tiposPropiedad = [
    'Casa', 'Departamento', 'Oficina', 'Local Comercial',
    'Terreno', 'Bodega', 'Penthouse', 'Duplex', 'Estudio', 'Nave Industrial'
  ];

  return (
    <div className="propiedad-form-container">
      <div className="form-header">
        <h3 className="form-title">
          {propiedad ? 'Editar Propiedad' : 'Nueva Propiedad'}
        </h3>
        <p className="form-subtitle">
          {propiedad
            ? 'Modifica los datos de la propiedad seleccionada.'
            : 'Completa la información para registrar una nueva propiedad.'
          }
        </p>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-icon">⚠</div>
          <div className="error-text">{error}</div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="propiedad-form">
        <div className="form-grid">
          <div className="form-section">
            <h4 className="section-title">Información Básica</h4>

            <div className="form-field">
              <label className="form-label">
                Título <span className="required-indicator">*</span>
              </label>
              <input
                type="text"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Casa moderna en zona residencial"
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Descripción <span className="required-indicator">*</span>
              </label>
              <textarea
                name="descripcion"
                value={formData.descripcion}
                onChange={handleChange}
                required
                className="form-textarea"
                placeholder="Describe las características principales de la propiedad..."
                rows={4}
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Dirección <span className="required-indicator">*</span>
              </label>
              <input
                type="text"
                name="direccion"
                value={formData.direccion}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Ej: Av. Principal 123, Colonia Centro"
              />
            </div>

            <div className="form-field">
              <label className="form-label">
                Tipo <span className="required-indicator">*</span>
              </label>
              <select
                name="tipo"
                value={formData.tipo}
                onChange={handleChange}
                required
                className="form-select"
              >
                <option value="">Seleccionar tipo</option>
                {tiposPropiedad.map(tipo => (
                  <option key={tipo} value={tipo}>{tipo}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="form-section">
            <h4 className="section-title">Detalles Numéricos</h4>

            <div className="form-field">
              <label className="form-label">
                Precio <span className="required-indicator">*</span>
              </label>
              <input
                type="number"
                name="precio"
                value={formData.precio}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>

            <div className="form-row">
              <div className="form-field half-width">
                <label className="form-label">
                  Habitaciones <span className="required-indicator">*</span>
                </label>
                <input
                  type="number"
                  name="habitaciones"
                  value={formData.habitaciones}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>

              <div className="form-field half-width">
                <label className="form-label">
                  Baños <span className="required-indicator">*</span>
                </label>
                <input
                  type="number"
                  name="banos"
                  value={formData.banos}
                  onChange={handleChange}
                  required
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
              </div>
            </div>

            <div className="form-field">
              <label className="form-label">
                Metros Cuadrados <span className="required-indicator">*</span>
              </label>
              <input
                type="number"
                name="metrosCuadrados"
                value={formData.metrosCuadrados}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="0.00"
                min="0"
                step="0.01"
              />
            </div>

            <div className="checkbox-field">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  name="disponible"
                  checked={formData.disponible}
                  onChange={handleChange}
                  className="checkbox-input"
                />
                <span className="checkbox-text">Propiedad Disponible</span>
              </label>
            </div>
          </div>
        </div>

        <div className="form-section full-width">
          <h4 className="section-title">Imágenes de la Propiedad</h4>
          <div className="form-field">
            <label className="form-label">Seleccionar Archivos de Imagen</label>
            <input
              type="file"
              name="imagenes"
              onChange={handleFileChange}
              className="form-input"
              multiple // Permite seleccionar múltiples archivos
              accept="image/*" // Limita la selección a tipos de imagen
            />
            <p className="field-hint">
              Selecciona uno o más archivos de imagen (JPG, PNG, etc.).
            </p>
          </div>

          {/* Mostrar URLs de imágenes existentes */}
          {existingImageUrls.length > 0 && (
            <div className="existing-images-preview">
              <h5>Imágenes actuales:</h5>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                {existingImageUrls.map((url, index) => (
                  <img
                    key={index}
                    src={url}
                    alt={`Imagen ${index + 1}`}
                    style={{ width: '100px', height: '100px', objectFit: 'cover', border: '1px solid #ccc' }}
                  />
                ))}
              </div>
              <p className="field-hint">
                Si subes nuevas imágenes, estas se añadirán a las existentes.
              </p>
            </div>
          )}

          {/* Mostrar nombres de archivos seleccionados */}
          {selectedFiles.length > 0 && (
            <div className="selected-files-preview">
              <h5>Archivos seleccionados para subir:</h5>
              <ul>
                {selectedFiles.map((file, index) => (
                  <li key={index}>{file.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={onCancel}
            className="btn-secondary"
            disabled={isSubmitting}
          >
            Cancelar
          </button>
          <button
            type="submit"
            className={`btn-primary ${isSubmitting ? 'loading' : ''}`}
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="loading-spinner"></span>
                Guardando...
              </>
            ) : (
              propiedad ? 'Actualizar Propiedad' : 'Guardar Propiedad'
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default PropiedadForm;
